INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (2, '2.2', '船舶のP&Iクラブは国際ｸﾞﾙｰﾌﾟに属しているか？', 'Is the vessel’s P and I Club a member of the International Group?', 16);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (2, '2.3', '会社の定めた手順書類はISMｺｰﾄﾞの要求に適合しているか？', 'Do the operator’s procedures manuals comply with ISM Code requirements?', 17);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (2, '2.4', '会社の代表者は少なくとも年二回は訪船しているか？', 'Does the operator''s representative visit the vessel at least bi-annually?', 18);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (2, '2.5', '最近の内部監査報告があり、不適合事項処理のためのｸﾛｰｽﾞﾄﾞｱｳﾄｼｽﾃﾑは適切に実施されているか？最近とは12ヶ月未満を意味する。', 'Is the last operator’s internal audit report available and is a close-out system in place for dealing with non-conformities? Current means less than 12 months.', 19);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (2, '2.6', '船長は安全管理ｼｽﾃﾑを見直し、不具合事項を会社に報告し、会社は船長の見直しに対して返事を返しているか？', 'Does the master review the safety management system, report to the operator on any deficiencies and does the operator respond to the Master’s review?', 20);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (2, '2.7', '船級検査報告書は適切にﾌｧｲﾙされているか？', 'Are class survey reports adequately filed?', 21);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (2, '2.8', '船級からの付帯条件・勧告・覚え書き・表記などはないか？', 'Is the vessel free of conditions of class or significant recommendations, memoranda or notations?n.', 22);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (2, '2.9', '船舶が拡充（詳細）検査プログラムの大正になっている場合、検査報告書は適切に管理されているか？', 'If the vessel is subject to the Enhanced Survey Programme, is the report file adequately maintained?', 23);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (2, '2.10', 'CAS適用の場合、(主官庁への）CAS最終報告･検証記録のｺﾋﾟｰがあるか？', 'If the vessel is subject to the Condition Assessment Scheme (CAS), are copies of the Condition Assessment Scheme Final Report and Review Record available?', 24);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (2, '2.11', '管理会社により、CAS検査計画書は作成･提出済であるか？', 'Has a Survey Plan for the CAS been completed and submitted by the operator?', 25);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (2, '2.12', '船舶はCAPに登録されているか？', 'Has the vessel been enrolled in a Classification Society Condition Assessment programme (CAP)?', 26);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (3, '3.1', '実際の配員は最小安全配員証書の要件に適合又は上回っているか？', 'Does the manning level meet or exceed that required by the Minimum Safe Manning Document?', 1);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (3, '3.2', '労働時間を調整し、疲労を最小限とするための、STCW或いは旗国の規則を守っているか？', 'Are the STCW and flag Administration’s regulations that controls hours of work to minimise fatigue being followed?', 2);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (3, '3.3', '休息時間の記録は維持されており、それは海上労働条約或いはSTCW条約にに適合しているか？ ', 'Do all personnel maintain hours of rest records and are the hours of rest in compliance with MLC or STCW requirements?', 3);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (3, '3.4', '全ての乗組員は共通の言語で意志疎通が出来るか？', 'Are all personnel able to communicate effectively in a common language?　 ', 4);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (3, '3.5', '全ての航海士はﾌﾞﾘｯｼﾞﾘｿｰｽﾏﾈｰｼﾞﾒﾝﾄ、またはﾌﾞﾘｯｼﾞﾁｰﾑﾏﾈｰｼﾞﾒﾝﾄｺｰｽの訓練に参加したことがあるか？', 'Have all deck officers attended either a Bridge Resourse Management, or BridgeTeam Management course?', 5);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (3, '3.6', '適用される場合、船長は操船訓練ｺｰｽに参加したことがあるか？', 'Has the master attended a ship handling course where applicable?', 6);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (3, '3.7', '船舶が高電圧装置を備えている場合、乗組員は適切な訓練を受けているか?', 'If the vessel fitted with High Voltage equipment, is staff suitably trained?', 7);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (3, '3.8', 'ｹﾐｶﾙを運送する船舶の場合、定期的で適切な医療検査の正式なﾌﾟﾛｸﾞﾗﾑが実施されているか？', 'Where the vessel carries chemicals, has a formal programme of regular　and appropriate medical examinations for personnel been implemented?', 8);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (3, '3.9', '検船時、乗船している職員の情報は、SIREのWebsiteに登録されたものを精確に反映したものが掲示されているか？', 'Does the officers’ matrix posted for the vessel on the SIRE website accurately reflect the information relating to the officers on board at the time of the inspection?', 9);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (3, '3.10', '貨物の移送に関し責任を有する職員は、運送している貨物の種類に適した訓練証書を所持しているか?', 'Are those officers who have immediate responsibility for cargo transfer, in possession of the Certificates of Specialized Training as applicable to the type of cargo being carried?', 10);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (3, '3.11', '管理会社はOCIMFのｶﾞｲﾄﾞﾗｲﾝに合致した薬物･ｱﾙｺｰﾙの管理に関する方針を用意しているか？', 'Does the operator’s Drug and Alcohol policy meet OCIMF guidelines?', 11);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (3, '3.12', '定義された血中ｱﾙｺｰﾙ濃度の最大許容値', 'What was the Operator’s defined maximum level of blood alcohol content', 12);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (3, '3.13', '予告無しのﾄﾞﾗｯｸﾞﾃｽﾄの頻度', 'What was the recorded the frequency of unannounced drug testing', 13);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (3, '3.14', '予告無しのｱﾙｺｰﾙﾃｽﾄの頻度', 'What was the recorded the frequency of unannounced alcohol testing', 14);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (3, '3.15', '最新の予告無しのｱﾙｺｰﾙﾃｽﾄの実施日（船内）', 'What was the date of the last unannounced on-board alcohol test', 15);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (3, '3.16', '最新の予告無しｱﾙｺｰﾙﾃｽﾄ・ﾄﾞﾗｯｸﾞﾃｽﾄの実施日（外部業者による）', 'What was the date of the last unannounced drug and alcohol test undertaken by an external agency', 16);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.1', '安全航海に関する十分な船主の指示書・手順書が備えられているか？', 'Is the vessel provided with adequate operator’s navigation instructions and procedures?', 1);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.2', '船長は自身の航海当直基準を作成し、船橋命令簿も用意しているおり、当直航海士は読み、理解しｻｲﾝしているか？', 'Has the Master written his own Standing Orders and are Bridge Orders being completed and have the deck officers countersigned them as being read and understood?', 2);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.3', '航海日誌・ﾍﾞﾙﾌﾞｯｸは正確に維持され、一般航海中や水先人乗船時も含めて全ての航海活動の十分な記録があるか？', 'Are deck log books and engine movement (bell) books correctly maintained and is an adequate record being kept of all the navigational activities, both at sea and under pilotage?', 3);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.4', '船舶操縦性能図が掲示されているか？', 'Are the vessel’s manoeuvring characteristics displayed on the bridge?', 4);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.5', '入出港前に実施する船橋設備のﾃｽﾄ手順は適切であるか？　', 'Are procedures in place for the testing of bridge equipment before arrival and departure?', 5);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.6', '毎航海当直終了後の防火及び安全のための巡検を実施し、記録しているか？', 'Are records maintained of fire and safety rounds being completed after each watch?', 6);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.7', '入出港前ﾁｪｯｸﾘｽﾄ・当直引継ﾁｪｯｸﾘｽﾄ・ﾊﾟｲﾛｯﾄ/船長間で取り交わすﾁｪｯｸﾘｽﾄ・ﾊﾟｲﾛｯﾄｶｰﾄﾞは維持されているか？', 'Are checklists for pre-arrival, pre-departure, watch handover,  Pilot-Master exchange and pilot card effectively completed?', 7);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.8', 'ﾐﾆﾏﾑUKCと浅水影響に関する手引書が用意されているか？', 'Does the operator provide guidance on minimum under keel clearance and squat?', 8);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.9', '航海及び錨泊時の全てのｽﾃｰｼﾞで船橋は適切に人員配置されており、見張配置は適切である。', 'Has the bridge been adequately manned at all stages of the voyage and at Anchor and were lookout arrangement adequate?', 9);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.10', '装備されている航海計器は船型に適したものであり、作動良好であるか？', 'Is navigation equipment appropriate for the size of the vessel and in good order? ', 10);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.10.1', '全世界衛星航法ｼｽﾃﾑ又は全地上系無線航法ｼｽﾃﾑ受信機（ＧＰＳ）', 'A receiver for a global navigation satellite system or terrestrial navigation radionavigation system', 11);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.10.2', 'ﾅﾌﾞﾃｯｸｽ受信機', 'A Navtex receiver', 12);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.10.3', '汽笛・号鐘・銅鑼', 'A whistle, bell and gong', 13);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.10.4', '形象物', 'Shapes', 14);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.10.5', '適切に修正された標準磁気ｺﾝﾊﾟｽ', 'A properly adjusted standard magnetic compass', 15);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.10.6', 'ステアリング磁気コンパス', 'A steering magnetic compass', 16);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.10.7', '方位測定手段', 'Means for taking bearings', 17);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.10.8', '予備磁気ｺﾝﾊﾟｽ', 'A spare magnetic compass', 18);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.10.9', '電話', 'A telephone', 19);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.10.10', '昼間信号灯', 'A daylight signalling lamp', 20);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.10.11', '国際船舶自動識別装置（AIS）', 'An automatic identification system (AIS)', 21);
