INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.11', '航海灯は良好な状態であるか？', 'Are navigation lights in good order?', 63);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.12', '航海当直警報ｼｽﾃﾑが装備されている場合、船舶が海上にある場合は常時作動しているか?', 'If a bridge navigational watch alarm system (BNWAS) is fitted is it operational at all times when the vessel is at sea?', 64);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.13', '標準磁気ｺﾝﾊﾟｽ及びｼﾞｬｲﾛｺﾝﾊﾟｽは作動しており、適切に保守・調整されているか？', 'Are the Standard Magnetic compass and Gyro compases operational, properly maintained and adjusted?', 65);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.14', '自動から手動への操舵切替え手順が掲示されているか？', 'Are auto to manual steering changeover procedures clearly identified?', 66);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.15', '河川航行時及び制限区域航行時は自動操舵から手動操舵への切替が記録されているか？', 'Are auto to manual steering changeover recorded during periods of river transits and when navigating through restricted waters?', 67);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.16', 'ｼﾞｬｲﾛｺﾝﾊﾟｽ・磁気ｺﾝﾊﾟｽの誤差が定期的に測定・記録されているか？', 'Are regular gyro and magnetic compass errors being taken and are they being recorded?', 68);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.17', 'VDRの取扱のための文書化された手順があり、航海士は事故が発生した時VDRﾃﾞｰﾀを維持する手順に慣れていなければならない。', 'Is there a documented procedure for the operation of the VDR and are the Deck Officers familiar with procedure to retain the VDR data in the evernt of an incident?', 69);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.18', '全ての海図・水路書誌類・その他の書誌類を船内に備え、最新にし、更新する手順が確立されているか？', 'Has a system been established to ensure that all Charts, nautical publications and other publications are on board, current and maintained up to date?', 70);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.19', '航海のため承認された手段として紙海図のみを備えている船に於いては当該航海のために要求される全ての海図が備えられており、完全に改補されている.', 'If the vessel is provided solely with paper charts as an approved means of navigation are all charts required for the intended voyage of the vessel on board and are these fully corrected?', 71);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.20', '前航海に使用された海図は適切なものであるか？', 'Were the charts used for the previous voyage appropriate?', 72);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.21', '船舶がECDISを承認された航海手段として装備している場合、船長及び当直航海士は、一般的な及び型式特定のECDISの習熟訓練を受講していることを示す適切な証書を示すことができるか？', 'If the vessel is equipped with an Electronic Chart Display and Information System (ECDIS) as an approved means of navigation, are the Master and deck watchkeeping officers able to produce appropriate documentation that generic and type-specific ECDIS familiarisation has been undertaken?', 73);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.22', '船舶がECDISを装備している場合、SOLASの要求事項に合致しているか?', 'If the vessel is provided solely with an Electronic Chart Display and Information System (EDCIS) does it meet the requirements of SOLAS? ', 74);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.23', '本船はこれまで安全に航行しており、国際規則にも適合しているか？', 'Has the vessel been safely navigated and in compliance with international regulations?', 75);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.24', '音響測深儀は起動毎に日時がﾏｰｸされているか？', 'Is the echo sounder recorder marked with a reference date and time on each occasion it is switched on?', 76);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.25', '前航海、包括的な航路計画が使用されており、ﾊﾞｰｽからﾊﾞｰｽまでの全航程について記述されていたか？', 'Was a comprehensive passage plan available for the previous voyage and did it cover the full voyage from berth to berth?', 77);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.26', '前航海、ﾊﾟﾗﾚﾙｲﾝﾃﾞｯｸｽを使用することも含めた船位測定が十分に実施され、測定頻度も航海計画に従って行なわれていたか？', 'Was position fixing including the use of parallel indexing satisfactory throughout the previous voyage and the frequency of plotted fixes in accordance with the passage plan?', 78);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.27', 'ﾊﾟｲﾛｯﾄ乗船中も船位を適切に監視していたか？', 'During pilotage, was the position of the vessel adequately monitored?', 79);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.28', 'GPSは正確なﾃﾞｰﾀに調整されているか？', 'Has the GPS been adjusted to the correct datum?', 80);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (4, '4.29', '航行警報を処理する適切なｼｽﾃﾑが確立されており、海図に記入されているか？', 'Is there an adequate system for dealing with navigation warnings and are they being charted?', 81);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.1', '安全担当者が指名され、その任務遂行のために訓練されており、その任務に関連する職務を効果的に実行していることを証明する根拠があるか？', 'Has a safety officer been designated, trained to undertake this role and is there evidence to show that they are effectively performing duties associated with this role?', 1);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.2', '職員は消防・救命・緊急時用の設備に習熟しているか？', 'Are the ship’s officers familiar with the operation of fire fighting, life saving and other emergency equipment?', 2);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.3', '防護服・安全靴・視聴覚保護具・安全ﾊｰﾈｽ・ｹﾐｶﾙ保護具などが支給され、必要に応じて装着されているか？', 'Is personal protective equipment such as boiler suits, safety footwear, eye and ear protection, safety harnesses and chemical protective equipment etc. provided and as required, being worn?', 3);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.4', '全ての懐中電灯はｶﾞｽ危険区域での使用を承認されたものであるか？', 'Are all hand torches approved for use in gas-hazardous areas?', 4);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.5', '定期的な安全ﾐｰﾃｨﾝｸﾞが開催され、議事録が残されているか？又、船主は会社側の返事を提供しているか？', 'Are regular safety meetings held, are the minutes recorded and does the operator provide shore management responses?', 5);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.6', '事故やトラブル・不適合・ﾆｱﾐｽの場合に報告、調査、ｸﾛｰｽﾞﾄﾞアウト手順が定められているか？この手順は適切な報告、記録、調査及びｸﾛｰｽﾞﾄﾞｱｳﾄ実施事項を含んでいるか？', 'Is there a procedure for the reporting, investigation and close-out of accidents, incidents, non-conformities and near misses? Is this procedure being followed up with proper reporting, recording, Investigation and close out of action items?', 6);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.7', 'ISGOTTの船・陸間安全ﾁｪｯｸﾘｽﾄ（SSSCL）が用意され、その規定は遵守されているか？', 'Is a completed ISGOTT Ship/Shore Safety Check List (SSSCL) available and are its provisions being complied with?', 7);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.8', '喫煙規則が掲示され、遵守されているか？又、喫煙室は適切に指定されているか？', 'Are smoking regulations posted and being adhered to and are smoke rooms adequately identified?', 8);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.9', '港内では全てのﾄﾞｱ・開口部・窓の閉鎖は確実に実施されているか？', 'Are external doors, ports and windows kept closed in port?', 9);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.10', '居住区の大気圧は、周囲の圧力よりも高く維持されているか？', 'Is the accommodation space atmosphere being maintained at a higher pressure than that of the ambient air? ', 10);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.11', '甲板・倉庫・内部区画に置かれた用具類は確実に固縛されているか？', 'Is all loose gear on deck, in stores and in internal spaces properly secured?', 11);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.12', '新乗船者のための習熟訓練手順が用意されているか？', 'Is there a procedure for familiarization for new personnel?', 12);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.13', '緊急時対応手順に関する操練は実施されているか？', 'Are drills for emergency procedures being carried out?　', 13);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.14', '救命艇・防火操練は定期的に実施されているか？', 'Are lifeboat and fire drills regularly held?', 14);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.15', '救命設備を使用しての定期的な訓練が行なわれているか？', 'Is regular training in the use of life-saving equipment being undertaken?', 15);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.16', '寄港地に関する船舶保安記録は維持されているか？', 'Are ship security records related to port calls being maintained?', 16);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.17', '船舶保安計画に関連する保安記録は時事されているか？', 'Are ship security records related to the ship security plan being maintained? ', 17);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.18', '船舶保安職員は任命され、その職務を実行する訓練を受けているか？', 'Has a security officer been designated and trained to undertake this role?', 18);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.19', '無許可者の乗船を防ぐための適切なﾃﾞｯｷﾜｯﾁが維持されているか？', 'Is an adequate deck watch being maintained to prevent unauthorized access?', 19);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.20', '船舶は適切な閉鎖区画立入手順が用意しているか？', 'Has the vessel adequate enclosed space entry procedures?', 20);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.21', 'ﾎﾟﾝﾌﾟﾙｰﾑへの立入手順が遵守されているか？', 'Are pump room entry procedures being complied with?', 21);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.22', 'ﾎﾟﾝﾌﾟﾙｰﾑ内は適切に換気されているか？', 'Are pump room spaces adequately ventilated?', 22);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.23', 'ﾎﾟﾝﾌﾟﾙｰﾑの防火・防水ﾀﾞﾝﾊﾟｰは操作方法が明瞭に表示され、良好な状態であるか？', 'Are pump room fire and flooding dampers clearly marked as to their operation and in good order?', 23);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.24', 'ﾎﾟﾝﾌﾟﾙｰﾑ底部より意識の無い人間を吊り上げるための恒久的な設備（適切な担架・ﾊｰﾈｽを含む）が装備されており、良好な状態であるか？', 'Are permanent arrangement provided for lifting an incapacitated person from the cargo and, if applicable, the ballast pumproom, including provision of a suitable stretcher or harness and is the equipment in good order?', 24);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.25', 'ﾊﾟｲﾌﾟﾀﾞｸﾄを含むｶｰｺﾞﾀﾝｸの隣接区画は、滞留ｶﾞｽの有無について定期的に監視されているか？', 'Are spaces adjacent to cargo tanks, including pipe ducts, regularly monitored for accumulations of gas?', 25);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.26', '非貨物区画に固定式ｶﾞｽ検知装置が装備されている場合、記録計や警報は良好であるか？', 'Where a fixed system to monitor flammable atmospheres in non-cargo spaces is fitted, are recorders and alarms in order?', 26);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.27', '携帯式ｶﾞｽ・酸素検知器は、積荷の種類に適したものを所持しており、それらは良好な状態であり、定期的ﾃｽﾄ及び較正の記録があるか？', 'Are portable gas and oxygen analysers appropriate to the cargoes being carried and are they in good order and is there a record of regular testing and calibration?', 27);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.28', '職員は、携帯式ｶﾞｽ・酸素検知器の使用と較正について理解しているか？', 'Are officers familiar with use and calibration of portable oxygen and hydrocarbon analysers?', 28);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.29', '固定式・携帯式検知器の、ｽﾊﾟﾝ較正用の標準ｶﾞｽを所持しているか？', 'Is　span calibration gas available for the types of fixed and portable analysers on board?', 29);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.30', 'IGｼｽﾃﾑを装備している船舶は、可燃性ｶﾞｽ検知器は、酸素濃度が不足している場合でも計測可能であり、良好な状態であるか？', 'On vessels fitted with an inert gas system, are instruments capable of measuring hydrocarbon content in an oxygen deficient atmosphere available and in good order? ', 30);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.31', '取り扱う可能性のある有毒ｶﾞｽに適した検知器が備えられ、良好な状態であるか？', 'Where toxic gases may be encountered, are appropriate toxic gas detection analysers available and in good order?', 31);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.32', 'ISGOTTの9.4章及びOCIMFｶﾞｲﾄﾞﾗｲﾝに準拠した高熱作業手順が用意されているか？', 'Are hot work procedures in accordance with the recommendations of ISGOTT Section 9.4 and OCIMF guidelines?', 32);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.33', '電気溶接機器は良好な状態であり、文書化された安全ｶﾞｲﾄﾞﾗｲﾝが現場に用意されているか？', 'Is electric welding equipment in good order and are written safety guidelines available on site?', 33);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.34', 'ｶﾞｽ溶接機器及び切断機器は良好な状態であるか？', 'Is gas welding and burning equipment in good order?', 34);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.35', '酸素・ｱｾﾁﾚﾝの予備ｼﾘﾝﾀﾞｰは、居住区・機関室以外の換気良好な特定の倉庫に別々に格納され、明瞭に表示されているか？', 'Are spare oxygen and acetylene cylinders stored apart in a dedicated storage and is the storage in a clearly marked, well-ventilated position outside the accommodation and engine room?', 35);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.36', '本船ｽﾍﾟｯｸに合った、救命設備訓練ﾏﾆｭｱﾙが用意されているか？', 'Are ship-specific life-saving equipment training manuals available?', 36);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.37', '本船ｽﾍﾟｯｸの、救命設備整備指針があり、週単位・月単位の点検が実施されているか？', 'Are ship-specific life-saving equipment maintenance instructions available and are weekly and monthly inspections being carried out?', 37);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.38', '非常配置表及び救命胴衣の着用説明書は掲示されているか？', 'Are muster lists and lifejacket donning instructions displayed?', 38);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.39', '救命艇の負荷離脱装置に関する整備・ﾃｽﾄ計画が用意されているか？', 'Is there a maintenance and test schedule for lifeboat on-load release gear?', 39);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.40', '救命艇は、艤装品・進水装置を含めて良好な状態であるか？', 'Are lifeboats, including their equipment and launching mechanisms, in good order?', 40);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.41', '救命艇・救命筏の操作説明書は掲示されているか？', 'Are lifeboat and liferaft operating instructions displayed?', 41);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.42', '救助艇は、艤装品・進水装置を含めて良好な状態であるか？', 'Is the rescue boat, including its equipment and launching arrangement, in good order?', 42);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.43', '救命筏は良好な状態であるか？', 'Are liferafts in good order?', 43);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.44', '水圧離脱装置が設置されている場合は、正しく装備され良好な状態であるか？', 'Are hydrostatic releases, where fitted, correctly attached and in good order?', 44);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.45', '救命浮環・自己点火灯・綱・急速離脱装置・自己発煙信号は良好な状態であるか？', 'Are lifebuoys, lights, buoyant lines, quick release mechanisms and self-activating smoke floats in good order?', 45);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.46', '救命胴衣は良好な状態であるか？', 'Are lifejackets in good order?', 46);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.47', 'ｲﾏｰｼﾞｮﾝｽｰﾂは良好な状態であるか？', 'Are immersion suits in good order?', 47);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.48', '救命索発射器を含む火薬類は有効期限内にあり、良好な状態であるか？', 'Are pyrotechnics, including line throwing apparatus, in date and in good order?', 48);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.49', '救命設備の設置箇所にはIMOｼﾝﾎﾞﾙﾏｰｸが表示されているか？', 'Are the locations of life saving appliances marked with IMO symbols?', 49);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.50', '本船ｽﾍﾟｯｸに合った、火災訓練ﾏﾆｭｱﾙが用意されているか？', 'Are ship-specific fire training manuals available?', 50);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.51', '本船ｽﾍﾟｯｸに合った、火災安全操作ﾌﾞｯｸﾚｯﾄが用意されているか？', 'Are ship-specific fire safety operational booklets available?', 51);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.52', '本船ｽﾍﾟｯｸに合った消火設備の手引書が用意されており、1週間毎及び1ヶ月毎の検査がおこなわれているか？', 'Are ship-specific fire fighting equipment maintenance instructions available and are weekly and monthly inspections being carried out?　', 52);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.53', '泡消火液のｻﾝﾌﾟﾙが定期的にﾃｽﾄされていることを示す記録が用意されているか？', 'Are records available to show that samples of foam compound have been tested at regular intervals?', 53);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.54', '居住区内に火災制御図が掲示されており、克つ居住区外部にもｺﾋﾟｰが用意されており、正しく表示されているか？', 'Is a fire control plan exhibited within the accommodation, is a copy also available externally and is equipment correctly marked on it? ', 54);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.55', '消火主管・消火ﾎﾟﾝﾌﾟ・ﾎｰｽ・ﾉｽﾞﾙは良好で、直ちに使用できるように準備されているか？', 'Are fire mains, pumps, hoses and nozzles in good order and available for immediate use?', 55);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.56', '泡消火装置の遮断弁は明瞭に表示され、良好な状態であるか？', 'Are isolating valves in fire and foam system lines clearly marked and in good order?', 56);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.57', '国際陸上連結具は直ちに使用可能で、格納場所は明瞭にﾏｰｸされているか？', 'Is the International shore fire connection readily available externally and is the location clearly marked?', 57);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.58', '固定式火災探知装置及び警報は良好な状態であり、定期的にﾃｽﾄされているか？', 'Are fixed fire detection and alarm systems in good order and tested regularly?', 58);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.59', '主甲板・ﾎﾟﾝﾌﾟﾙｰﾑ・機関室その他の固定消火装置（装備されている場合）は、良好な状態であり、操作説明書が明瞭に掲示されているか？', 'Are the main deck, pump room, engine room and other fixed fire extinguishing systems, where fitted, in good order and are clear operating instructions posted?', 59);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.60', '非常用消火ﾎﾟﾝﾌﾟは完全に使用可能で、起動説明書も明瞭に表示されているか？', 'Is the emergency fire pump in full operational condition and are starting instructions clearly displayed?', 60);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.61', '携帯式消火器は良好な状態であり、取扱い説明書も明瞭に表示されているか？', 'Are portable fire extinguishers in good order with operating instructions clearly marked?', 61);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.62', '消防員装具及び呼吸具は良好な状態であり、完全に充填されたｴｱｰｼﾘﾝﾀﾞｰが取り付けられ、直ちに使用可能であるか？', 'Are firemen''s outfits and breathing apparatus in good order, fitted with fully pressurised air cyliders and ready for immediate use?', 62);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.63', '居住区・ﾎﾟﾝﾌﾟﾙｰﾑ・機関室の非常脱出用呼吸具は良好な状態であり、直ちに使用可能であるか？', 'Are emergency escape breathing devices (EEBD’s) in the accommodation, pump room and engine room in good order and ready for immediate use?', 63);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.64', '居住区、換気ﾌｧﾝの非常停止装置は供用ｽﾍﾟｰｽを明瞭に表示され、定期的ﾃｽﾄ及び保守整備を示す根拠があるか？', 'Are accommodation and ventilation fan emergency stops clearly marked to indicate the spaces they serve and is there evidence of regular testing and maintenance?　', 64);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.65', 'ﾌｧｲﾔｰﾌﾗｯﾌﾟ板は供用ｽﾍﾟｰｽを明瞭に表示され、定期的なﾃｽﾄ及び保守整備を示す根拠があるか？', 'Are fire flaps clearly marked to indicate the spaces they serve and is there evidence of regular testing and maintenance?', 65);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.66', '取扱っている全ての貨物、燃料油及びその他の製品ﾃﾞｰﾀｼｰﾄが船上に用意されており、全ての職員はこれに精通しているか?', 'Are Material Safety Data Sheets (MSDS) on board for all the cargo, bunkers and other products being handled and are all officers familiar with their use? ', 66);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.67', 'ｷﾞｬﾝｸﾞｳｪｲは準備されているか？', 'Is a gangway provided?', 67);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.68', '舷梯・ｷﾞｬﾝｸﾞｳｪｲ（歩み板）・ﾊﾟｲﾛｯﾄﾗﾀﾞｰが設置されている場合、それらは良好な状態であるか？', 'Are accommodation ladders, gangways, pilot ladders and pilot hoists, where fitted, in good order?', 68);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.69', '全ての乗下船手段は、安全ﾈｯﾄ・救命浮環・救命索の準備を含めて適切であるか？', 'Are all means of access satisfactory, including the provision of a safety net, lifebuoy and line?', 69);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.70', 'ﾊﾟｲﾛｯﾄの移乗設備は適切であるか？', 'Are pilot boarding and access arrangements satisfactory?', 70);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.71', '船首部への安全な通路は適切なものであるか？', 'Are safe access to the bow arrangements satisfactory?', 71);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.72', 'ﾍﾘｺﾌﾟﾀｰﾗﾝﾃﾞｨﾝｸﾞ又はｳｨﾝﾁﾝｸﾞｴﾘｱがある場合、ICSの指針に適合しているか？', 'If a helicopter landing or winching area is provided, does it meet ICS guidelines?', 72);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (5, '5.73', '船橋のｳｨﾝｸﾞをｳｨﾝﾁﾝｸﾞｴﾘｱとして使用する場合、全体に渡ってR/Aが行われているか?', 'If the bridge wing is used as a winching area, is a thorough risk assessment conducted?', 73);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.1', '油記録簿ﾊﾟｰﾄ1（機関区域）及びﾊﾟｰﾄ2（ｶｰｺﾞ・ﾊﾞﾗｽﾄ）は、正確に記入されているか？', 'Are the Engine Room (Part I) and Cargo (Part II) Oil Record Books (ORBs) correctly completed?', 1);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.2', '油記録簿ﾊﾟｰﾄ1に記載されている機関室のｽﾗｯｼﾞﾀﾝｸ・ﾋﾞﾙｼﾞﾀﾝｸ等は、IOPP証書のﾌｫｰﾑBで特定されたものと一致しているか？', 'Do the sludge and bilge tanks designated in Form B of the IOPP Certificate and those listed in the Oil Record Book Part I, agree?', 2);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.3', '油記録簿には汚染事故又は違反行為を疑わせるものは見られないか？', 'Are the Oil Record Books free of any pollution incidents or violations? ', 3);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.4', 'ｽﾛｯﾌﾟ及びﾀﾞｰﾃｨﾊﾞﾗｽﾄの処分は、適切に記録されており、MARPOLに従って実施されているか？', 'Have disposals of slops and dirty ballast been adequately recorded and were they in accordance with MARPOL?', 4);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.5', '機関室の油水・ｽﾗｯｼﾞをｶｰｺﾞﾀﾝｸ又はｽﾛｯﾌﾟﾀﾝｸへ入れて処分した場合、その事実は両方の油記録簿に記載されているか？この場合、受入れﾀﾝｸは空であり、移送の為の設備は船級に承認されているか？', 'If the disposal of engine room oily water or sludge to a cargo or slop tank has taken place, has the event been recorded in both Oil Record Books, was the receiving tank free of cargo and have the transfer arrangements been approved by class?', 5);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.6', '承認された、SOPEP（油濁防止緊急措置手引書）或いはSMPEP（有害液体物質のための海洋汚染船内緊急計画）が船内に備えられているか？', 'Is an approved MARPOL Shipboard Oil Pollution Emergency Plan (SOPEP) or Shipboard Marine Pollution Emergency Plan (SMPEP) provided?', 6);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.7', 'SOPEPには、油防除資材・保管場所・展開の方法・少量の漏油に対応する乗組員の役割分担も含まれているか？', 'Does the plan include a description of equipment, its location, a plan for deployment and specific crewmember duties for handling small spills? ', 7);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.8', 'IMOの沿岸連絡先ﾘｽﾄは最新であるか？又、船長は通報手続を理解しており、入港地の連絡先ﾘｽﾄは作成されているか？', 'Is the IMO Coastal Contact List up to date, is the master aware of port contact procedures and has a contact list been made for this port?', 8);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.9', 'USCGの承認したVRP（流失油事故対応計画）を所持しているか？', 'Is there a USCG approved Vessel Response Plan (VRP)?', 9);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.10', '事故対応全権委任者', 'Name of the OPA-90 Qualified Individual (QI):　', 10);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.11', '承認された揮発性有機複合物質管理計画を所持しているか?', 'Is the vessel in possess of an approved Volatile Organic Compounds (VOC) Management Plan?', 11);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.12', '職員はﾋﾞﾙｼﾞやｶｰｺﾞｽﾛｯﾌﾟの処分に関する法令(MARPOL)を理解しているか？', 'Are officers aware of the requirements of MARPOL with respect ｔo the disposal of bilge water and cargo slops?', 12);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.13', 'ｽｶｯﾊﾟｰﾌﾟﾗｸﾞは良好な状態で、有効にｾｯﾄされているか？', 'Is the condition of scupper plugs satisfactory and are scuppers effectively plugged?', 13);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.14', '主甲板の縁辺のｺｰﾐﾝｸﾞが設置されているか？', 'Is the ship fitted with a maindeck boundary coaming?', 14);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.15', '少量の漏油の処理を行なうための手段は、直ちに使用可能であるか？', 'Are means readily available for dealing with small oil spills? ', 15);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.16', '汚染の原因となる、隔壁・ﾊﾞﾙﾌﾞ・諸管の漏洩はないか？', 'Is the vessel free from any visible bulkhead, valve or pipeline leakage liable to cause pollution?', 16);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.17', 'ｶｰｺﾞ配管上の、海水弁・船外排出弁は適切に固縛・施錠又はﾌﾞﾗﾝｸ板が挿入されており、荷役開始前に完全に閉鎖されていることがﾁｪｯｸされているか？', 'Are cargo system sea and overboard valves suitably lashed, locked or blanked and are they thoroughly checked to ensure that they are fully closed prior to commencement of cargo transfer?', 17);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.18', 'ｶｰｺﾞ用ｼｰﾊﾞﾙﾌﾞが設置されている場合、汚染防止手段は適切で、良好な状態であり、定期的な漏洩の有無の監視が実施されているか？', 'If cargo sea suction valves are fitted, are adequate pollution prevention measures in place, are they in good order and regularly monitored for leakage?', 18);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.19', 'ﾊﾞﾗｽﾄﾗｲﾝがｶｰｺﾞﾀﾝｸ、燃料ﾀﾝｸを通過している場合、定期的にﾃｽﾄされ、その結果が記録されているか？', 'If ballast lines pass through cargo tanks and/or Bunker tanks are they tested regularly and the results recorded?', 19);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.20', 'ｶｰｺﾞﾏﾆﾎｰﾙﾄﾞの下に適切な漏油用のｺﾝﾃﾅと格子板があり、適切なﾄﾞﾚﾝ排出設備が設置されており、空であるか？', 'Are adequate manifold spill containers and gratings in place under the cargo manifolds, fitted with suitable drainage arrangements and are they empty?', 20);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.21', '燃料ﾊﾟｲﾌﾟは毎年ﾃｽﾄされているか？', 'Are bunker pipelines tested annually?', 21);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.22', '使用していないｶｰｺﾞ・ﾊﾞﾝｶｰﾏﾆﾎｰﾙﾄﾞは全てﾎﾞﾙﾄﾅｯﾄが締められており、全てのﾄﾞﾚﾝ・ﾍﾞﾝﾄ・不使用のｹﾞｰｼﾞ孔は適切なﾌﾞﾗﾝｸ板又はｷｬｯﾌﾟが施されているか？', 'Are unused cargo and bunker pipeline manifolds fully bolted and are all drains and vents and unused gauge stems, suitably blanked or capped?', 22);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.23', '全ての燃料油ﾀﾝｸ・潤滑油ﾀﾝｸのﾍﾞﾝﾄの周囲には適切な漏油防止用の囲いが設置されているか？', 'Are suitable spill containers fitted around all fuel, diesel and lubricating oil tank vents?', 23);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.24', '甲板油圧機器の周囲には適切な漏油防止用の囲いが設置されているか？', 'Is a suitable containment fitted around hydraulic and other deck machinery?', 24);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.25', 'ﾌｫｯｸｽﾙその他の内部区画の油水を処理する装置は適切であるか？', 'Are the arrangements for the disposal of oily water in the foc''s''le and other internal spaces adequate?', 25);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.26', 'ﾎﾟﾝﾌﾟﾙｰﾑにﾋﾞﾙｼﾞｱﾗｰﾑが設置されており、定期的にﾃｽﾄ・記録されているか？', 'Are pump room bilge high level alarms fitted, regularly tested and the results recorded?', 26);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.27', 'ﾎﾟﾝﾌﾟﾙｰﾑのﾋﾞﾙｼﾞ増加に対する適切な非常用処理装置が設置されているか？', 'Are adequate arrangements provided for pipeline draining and the disposal of pump room bilge accumulations?', 27);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.28', '油排出監視装置（ODME）が設置されている場合、良好な状態であり、最近のﾃｽﾄ記録が用意されているか？', 'If an ODME is fitted, is it in good order and is there evidence of recent testing?', 28);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.29', '油排出監視装置（ODME）が不作動であった場合、その事実は油記録簿に記録されているか？', 'If the ODME has not been operational, was the fact recorded in the Oil Record Book?', 29);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.30', '船級が承認いたﾊﾞﾗｽﾄ水及び沈殿物のﾏﾈｰｼﾞﾒﾝﾄﾌﾟﾗﾝが備えてあり、ﾊﾞﾗｽﾄ水入替えの記録が維持されているか？', 'Does the operator have a Class approved ballast water and sediments management plan and are records being maintained of all ballast water exchanges?', 30);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.31', '分離ﾊﾞﾗｽﾄ排出前に、ﾁｪｯｸ・ｻﾝﾌﾟﾘﾝｸﾞが可能であり、油分はないか？ ', 'Can the vessel check or sample segregated ballast prior to deballasting and are they free from oil?', 31);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.32', '機関室のﾋﾞﾙｼﾞ処理・排出装置は良好な状態であるか？', 'Are the engine room bilge oily water pumping and disposal arrangements in good order?', 32);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.33', '非常用ﾋﾞﾙｼﾞ排出装置は直ちに使用可能で、非常用ﾋﾞﾙｼﾞｻｸｼｮﾝは明瞭に表示されているか？又、非常用ﾋﾞﾙｼﾞ船外排出弁がある場合は、偶発的開放防止に対する警告が表示されているか?', 'Are emergency bilge pumping arrangements ready for immediate use; is the emergency bilge suction clearly identified and, where fitted, is the emergency overboard discharge valve provided with a notice warning against accidental opening?', 33);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.34', 'ｽﾗｯｼﾞﾎﾟﾝﾌﾟから直接船外排出に連結されている配管はないか？', 'Are dedicated sludge pumps free from any connection to a direct overboard discharge?', 34);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.35', '油水分離器は良好な状態であるか？', 'Is the oily water separator in good order?', 35);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.36', '油水分離機からのﾋﾞﾙｼﾞ船外排出弁の偶発的開放を防止するために、特別な警告周知の掲示がされているか？', 'Are specific warning notices posted to safeguard against the accidental opening of the overboard discharge valve from the oily water separator?', 36);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.37', '機関室の油水分離機が自動停止装置を備えていない場合、油記録簿ﾊﾟｰﾄ1に、特別区域内では使用しないとなっているか?', 'If the oily water separator is not fitted with an automatic stopping device, do entries in the Oil Record Book Part 1 indicate that it has not been used in a special Area? ', 37);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.38', '操舵機室の油分を含むﾋﾞﾙｼﾞを処理する装置は適切であるか？', 'Are the arrangements for the disposal of steering compartment oily bilge water adequate?', 38);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.39', '廃棄物管理計画、及び記録簿を定め,記録簿は正しく記載されているか？', 'Does the vessel have a garbage management plan and garbage record book and is the garbage record book being correctly completed?', 39);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.40', '船舶は適切な廃棄物貯蔵及び処理設備が備えられているか？', 'Does the vessel have adequate garbage storage and disposal facilities?', 40);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (6, '6.41', '船舶は船舶ｴﾈﾙｷﾞｰ効率管理計画を所持しているか。', 'Has the vessel a Ship Energy Efficiency Management Plan (SEEMP)?', 41);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (7, '7.1', 'ESPﾌｧｲﾙでは、船舶の構造に関して提起された情報はない？', 'Is the Enhanced Survey Programme file free from any information that raises concerns relating to the vessel’s structure?', 1);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (7, '7.2', '船体に関して、詳細な調査を必要とするような視認可能な欠陥はないか？', 'Is the hull free from visible structural defects that warrant further investigation?', 2);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (7, '7.3', '曝露甲板に関して、詳細な調査を必要とするような視認可能な欠陥はないか？', 'Are weather decks free from visible structural defects that warrant further investigation?', 3);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (7, '7.4', '構造物に関して、詳細な調査を必要とするような視認可能な欠陥はないか？', 'Is the superstructure free from visible structural defects that warrant further investigation?', 4);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (7, '7.5', '内部区画に関して、詳細な調査を必要とするような視認可能な欠陥はないか？', 'Are internal spaces free from visible structural defects that warrant further investigation?', 5);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (7, '7.6', 'ｶｰｺﾞﾀﾝｸ又はﾊﾞﾗｽﾄﾀﾝｸの甲板より目視した場合、それらは良好な状態であるか？', 'If any cargo and/or ballast tanks were sighted from the deck, were they in good order?', 6);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (7, '7.7', '乗組員によるｶｰｺﾞﾀﾝｸ・ﾊﾞﾗｽﾄﾀﾝｸ・ﾎﾞｲﾄﾞｽﾍﾟｰｽ・ﾄﾗﾝｸやｺｯﾌｧﾀﾞﾑの定期的な検査が実施されており、記録も維持されているか？', 'Are procedures in place to carry out regular inspections of cargo and ballast tanks, void spaces, trunks and cofferdams by the vessel’s personnel and are records maintained?', 7);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.1P', '最大積荷速度及び通気容量を含む安全荷役に関する管理会社の基本方針・ｶﾞｲﾀﾞﾝｽ・手順書が用意されているか？', 'Is the vessel provided with operator’s policy statements, guidance and procedures, including information on maximum loading rates and venting capacities with regard to safe cargo operations?', 1);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.2P', '適用される場合、ｶｰｺﾞ配管・ｲﾅｰﾄｶﾞｽ・ﾍﾞﾝﾄ装置などを表示した最新で容易に理解可能な配管図若しくは操作台がﾎﾟﾝﾌﾟﾙｰﾑ及び荷役制御室に備えられているか？', 'Are legible and up to date pipeline and/or mimic diagrams of cargo, inert gas and venting systems, as applicable, available in the pumproom(s) and cargo control area?', 2);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.3P', 'ｶｰｺﾞﾎﾟﾝﾌﾟの性能曲線図があり、該当する場合、運転速度に対応して示されているか？', 'Are cargo pump performance curves available, where applicable, for various speeds?', 3);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.4P', '分離ﾊﾞﾗｽﾄ装備船舶の場合、ｶｰｺﾞﾀﾝｸに荒天ﾊﾞﾗｽﾄを積載する場合に備えて安全な取扱い手順書が作成されているか？', 'Is a written procedure provided for the safe handling of heavy weather ballast in cargo tanks on segregated ballast tankers?', 4);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.5P', '積荷計算用のｺﾝﾋﾟｭｰﾀ又はﾌﾟﾛｸﾞﾗﾑが使用されている場合、船級の承認を受けているか？', 'If a loading computer or programme is in use, is it class approved?', 5);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.6P', 'ｺﾝﾋﾟｭｰﾀはその精度について定期的にﾃｽﾄされていることを示す記録があるか？', 'Are there records indicate that the operational accuracy of the load computer is tested regularly?', 6);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.7P', '船体強度・復元性情報は荷役計画書に盛り込まれおり、当直航海士は制限事項を理解しているか？', 'Is the stress and stability information included with the cargo plan and are any limitations understood by the watch officers?', 7);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.8P', '本船には、非損傷時復元性に関する固有の問題はないか？', 'Is the vessel free of inherent intact stability problems?', 8);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.9P', '損傷時復元性資料が用意されており、船長は通常復原性資料に従って貨物の積載をしていることを示すことができるか?', 'Are Damage Stability Verification guidelines available and can the Master demonstrate that the vessel in normally loaded in accordance with the Stability Information Booklet (SIB)? ', 9);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.10P', '船長は復原性資料の中の最悪の損傷状態を理解しているか？', 'Is the Master aware of the worst damage stability condition in the stability book?', 10);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.11P', '該当する場合、会社の作業ﾏﾆｭｱﾙは、荷役中不安定な状態になった場合に復元性を回復するための手順を含んでいるか？', 'Do the operator’s operating manuals include procedures for restoring stability in case of unstable conditions developing during cargo operations, where applicable?', 11);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.12P', '該当する場合、航海士は遊動水の危険性やｽﾛｯｼﾝｸﾞによる構造物への損傷を引起こす可能性について理解しているか？', 'Where applicable, are officers aware of the dangers of free surface effects and of the possibility of structural damage caused by sloshing in cargo tanks?', 12);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.13P', 'ｶｰｺﾞﾀﾝｸ・ﾊﾞﾗｽﾄﾀﾝｸはｽﾛｯｼﾝｸﾞ又は他の制限が無いか？', 'Are cargo and/or ballast tanks free of sloshing or other restrictions?', 13);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.14P', '全ての職員は緊急陸揚げ装置も含めたｶｰｺﾞ装置について理解しているか？', 'Are all officers familiar with the cargo system、including emergency discharge arrangements?', 14);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.15P', '全ての職員は積載貨物の輸送上の要求事項を理解しているか？', 'Are all officers familiar with the carriage requirements for the cargoes on board?', 15);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.16P', '荷役計画書が作成されており、詳細な貨物・ﾊﾞﾗｽﾄ手順が含まれており、当直職員はｻｲﾝしているか？', 'Has a cargo plan been prepared and does it contain a detailed sequence of cargo and ballast transfer　and has it been signed by the watch officers?', 16);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.17P', '荷役中の作業や記録されている作業は、荷役計画書にしたがって行なわれているか？', 'Are cargo operations being carried out and logged in accordance with the plan?', 17);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.18P', '全ての職員は、ｶｰｺﾞに起因する火災・漏洩・汚染に対応する緊急手順について理解しているか？', 'Are all officers aware of the emergency procedures for dealing with leakage, spillage or fire involving the cargo?', 18);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.19P', '船・陸間の会話による意思疎通に問題はないか？', 'Is the verbal communication between the ship and the shore adequate?', 19);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.20P', 'ｶｰｺﾞ・ﾊﾞﾗｽﾄ・ストリッピングポンプ、ｴﾀﾞｸﾀｰ及びこれらの関連機器及び制御装置は良好な状態であり、定期的なﾃｽﾄの記録が用意されているか？', 'Are the cargo, ballast and stripping pumps, eductors and their associated instrumentation and controls, in good order and is there recorded evidence of regular testing?', 20);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.21P', 'ｶｰｺﾞ・原油洗浄・ﾍﾞｰﾊﾟｰ・ｲﾅｰﾄｶﾞｽﾗｲﾝは良好な状態であり、定期的なﾃｽﾄの記録が用意されているか？', 'Are the cargo lines, vapour lines and inert gas lines in good order and is there recorded evidence of regular testing?', 21);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.22P', 'ｶｰｺﾞﾎﾟﾝﾌﾟ緊急停止装置は良好な状態であり、定期的なﾃｽﾄの記録が用意されているか？', 'Is the cargo pump emergency shut down system in good order and is there recorded evidence of regular testing?', 22);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.23P', 'ｶｰｺﾞ・ﾊﾞﾗｽﾄ装置のﾊﾞﾙﾌﾞは良好な状態であり、定期的なﾃｽﾄの記録が用意されているか？', 'Are the cargo and ballast system valves in good order and is there recorded evidence of regular testing?', 23);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.24P', 'ｶｰｺﾞ装置のｱﾚｰｼﾞ測定機器、ﾍﾞｰﾊﾟｰﾛｯｸ装置とUTI計測機器類は良好な状態であり、定期的なﾃｽﾄの記録が用意されているか？', 'Are the cargo system ullage gauges, vapour locks and UTI tapes in good order and is there recorded evidence of regular testing?', 24);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.25P', '遠隔・ﾛｰｶﾙの、温度・圧力ｾﾝｻｰとｹﾞｰｼﾞは良好な状態であり、定期的なﾃｽﾄの記録が用意されているか？', 'Are the remote and local temperature and pressure sensors and gauges in good order and is there recorded evidence of regular testing?', 25);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.26P', 'ｶｰｺﾞﾀﾝｸの高位液面警報及びｵｰﾊﾞｰﾌﾛｰ警報は良好な状態であり、定期的なﾃｽﾄの記録が用意されているか？', 'Are the cargo tank high level and overflow alarms in good order and is there recorded evidence of regular testing?', 26);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.27P', 'ｶｰｺﾞ加熱装置が設置・使用されている場合、それらは良好な状態であるか？又、定期的にﾃｽﾄされており、漏油は見られないか？', 'Where fitted and in use, is the condition of the cargo tank heating system satisfactory, is it regularly tested and is any observation tank free of oil?', 27);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.28P', '固定式液面計が設置されていない場合、荷役中の各ﾀﾝｸを同時計測するに十分な数の持ち運び式ﾃｰﾌﾟが備えてあり、使用されている場合、ﾍﾞｰﾊﾟｰﾛｯｸは較正されているか？', 'If fixed tank gauges are not fitted, are sufficient portable tapes provided to simultaneously gauge each tank being worked, if used with vapour locks are they calibrated?', 28);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.29P', '引火性や毒性のｶｰｺﾞを取り扱っている場合、閉鎖荷役が実施されているか？', 'If the vessel is handling volatile or toxic cargoes, is it operating in a closed condition?', 29);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.30P', '承認されたﾍﾞｰﾊﾟｰ制御ｼｽﾃﾑを用意しているか？', 'Is the vessel provided with an approved vapour control system?', 30);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.31P', 'ﾀﾝｸﾊｯﾁ・洗浄ﾎｰﾙ・覗き窓は、油蜜・気密を維持しているか？', 'Do tank hatches, tank cleaning apertures and sighting ports appear to be liquid and gas tight?', 31);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.32P', 'ｶｰｺﾞﾍﾞﾝﾄ装置は良好な状態であり、適切に使用されているか？', 'Is the cargo venting system in good order and being operated correctly?', 32);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.33P', 'SOLAS規定ﾍﾞﾝﾄの、二次的手段の要求に適合しているか？', 'Are SOLAS secondary venting requirements being complied with?', 33);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.34P', '共通ﾍﾞﾝﾄ装置から個々のﾀﾝｸを分離する遮断弁がある場合、有効な施錠装置があり、鍵は貨物の移送に関する全責任を有する責任者が管理しているか？', 'If stop valves are fitted which permit isolation of individual tanks from the common venting system, are they provided with positive locking arrangements and are the keys under the control of the person in overall charge of the cargo transfer?', 34);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.35P', 'P/Vﾊﾞﾙﾌﾞは良好な状態であるか？又、日常の定期整備の一環として、点検・清掃され、整備記録が用意されているか？', 'Are the P/V valves in good order, inspected and cleaned as part of a regular maintenance routine and are there records to support this?', 35);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.36P', 'ｲﾅｰﾄｶﾞｽ装置（IGS）が設置されているか？', 'Is the vessel fitted with an inert gas system?', 36);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.37P', '検船時に満足のいく状態でIGSが作動しているか？', 'Was the inert gas system in use and operating satisfactorily at the time of the inspection?', 37);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.38P', 'IGSの運転記録が用意されているか？', 'Is a log kept of inert gas operations?', 38);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.39P', '逆止弁の開放を含む、機器整備の記録が維持されているか？', 'Are records maintained of equipment maintenance, including the overhaul of the non-return valve?', 39);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.40P', 'IGS故障の場合の処置に関して、IMOのｶﾞｲﾄﾞﾗｲﾝに適合する会社の方針が提供されており、船長・一等航海士・各当直航海士は、その処置・方針を理解しているか？', 'Is an operator’s policy provided that complies with IMO guidelines in case of failure of the inert gas system and do the master, chief officer and the officers standing cargo watches understand this?', 40);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.41P', '各種計器・警報・運転停止装置・圧力計・酸素記録計を含むIGS装置は良好な状態であるか？', 'Is the inert gas system including instrumentation, alarms, trips and pressure and oxygen recorders, in good order?', 41);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.42P', 'IGSの供給酸素濃度は5%以下であるか？ ', 'Is the oxygen content of the inert gas delivery at or below 5%?', 42);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.43P', 'ｶｰｺﾞﾀﾝｸのﾍﾞｰﾊﾟｰｽﾍﾟｰｽは、正圧に維持されているか？', 'Are the vapour spaces in the cargo tanks being maintained at positive pressure?', 43);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.44P', 'ｶｰｺﾞﾀﾝｸのﾍﾞｰﾊﾟｰ酸素濃度は8%未満であるか？', 'Is the oxygen content in the cargo tanks below 8%?', 44);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.45P', '固定式酸素濃度計は、IGS装置使用直前に較正されているか？', 'Was the fixed oxygen analyzer calibrated immediately prior to use of the inert gas system?', 45);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.46P', '現場・船橋・CCRに酸素濃度計及び圧力計が設置されている場合、その数値は一致しているか？', 'Do the readings on the local, bridge and cargo control room oxygen and pressure recorders, where fitted, agree?', 46);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.47P', 'ﾃﾞｯｷｼｰﾙの水位は正常で、明瞭に目視できるか？', 'Is the liquid level in the deck seal correct and clearly visible?　', 47);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.48P', 'P/Vﾌﾞﾚｰｶｰは良好な状態であるか？', 'Does the P/V breaker appear to be in good order?', 48);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.49P', 'ﾀﾞﾌﾞﾙﾊﾙのｽﾍﾟｰｽはｲﾅｰﾃｨﾝｸﾞできるか？', 'Can double hull spaces be inerted?', 49);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.50P', 'COW設備は装備されているか？', 'Is the vessel fitted with a crude oil washing system?', 50);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.51P', '検船時にCOWは行われている場合、ﾀﾝｸの原油洗浄はIMOの要求に沿って実施されていたか？', 'If crude oil washing is being carried out are the tanks being Crude oil washed in accordance with IMO requirement?', 51);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.52P', 'COWの操作・設備ﾏﾆｭｱﾙは用意されていたか？', 'Is an approved Crude Oil Washing Operations and Equipment Manual provided?', 52);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.53P', 'COWを行なっている場合、原油洗浄計画及びﾁｪｯｸﾘｽﾄは完成されており、これに従っていたか？', 'If the vessel is crude oil washing, has a crude oil washing plan and checklist been completed and is It being followed? ', 53);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.54P', 'COW操作の責任者は認証されていたか？', 'Is the person in charge of COW operations suitably qualified?', 54);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.55P', 'COW設備は使用前に圧力ﾃｽﾄが行なわれていたか？', 'Do records indicate that the crude oil washing system was pressure tested prior to use?', 55);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.56P', 'COWを行なうﾀﾝｸの酸素濃度が携帯式酸素検知器でﾁｪｯｸされており、許容値以内であったか？', 'Do records indicate that oxygen readings of the tanks to be crude oil washed have been checked by portable meter and found to be within maximum permissible limits?', 56);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.57P', 'COWﾗｲﾝの圧力計は正常に作動しているか？', 'Are crude oil washing line pressure gauges working?　', 57);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.58P', 'ﾀﾝｸｸﾘｰﾆﾝｸﾞﾋｰﾀｰが装備されている場合、COWﾗｲﾝと効果的に分離されているか？', 'Is the tank cleaning heater, where fitted, effectively isolated from the crude oil washing line?', 58);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.59P', 'COWﾗｲﾝに接続されているその他のﾊｲﾄﾞﾗﾝﾄﾀｲﾌﾟのものは、確実にｼｰﾙされているか？', 'Are any hydrant-type connections on the crude oil washing lines securely sealed?', 59);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.60P', '前に行なったCOWの記録は維持されているか？', 'Are records maintained of previous COW operations?', 60);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.61P', '積荷時の、初期最大流量に関する注意事項は守られているか？', 'Are precautions relating to maximum flow rates during initial loading being observed?', 61);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.62P', '要求される静電気緩和時間は守られているか？', 'Are required settling periods being observed?', 62);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.63P', 'ｶｰｺﾞﾀﾝｸにﾍﾞｰﾊﾟｰﾛｯｸ装置があり、測深管が底部まで達していない場合、ｱﾚｰｼﾞ測定或いはｻﾝﾌﾟﾙ採取前の適切な静置時間に関する静電気注意事項が守られているか?', 'Where vapour locks are fitted to cargo tanks that are not fitted with full depth sounding pipes, are static electricity precautions taken to ensure that the appropriate relaxation period elapses prior to ullaging or sampling?', 63);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.64P', '金属ﾃｰﾌﾟ、その他の検尺・ｻﾝﾌﾟﾘﾝｸﾞ機具は、ﾀﾝｸ内部に入れる前に有効なﾎﾞﾝﾃﾞｨﾝｸﾞを確保しているか？', 'Are metal tapes and other gauging or sampling devices effectively bonded before being introduced into tanks?', 64);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.65P', '合成繊維ではなく、天然繊維のﾛｰﾌﾟが検尺に使用されているか？', 'Are natural fibre ropes, as opposed to synthetic, used for dipping etc.?', 65);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.66P', '可搬式ﾀﾝｸ洗浄ﾎｰｽが使用される場合、導通ﾃｽﾄを実施し、結果を記録しているか？', 'If portable tank cleaning hoses are used, are continuity tests carried out and the results recorded?', 66);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.67P', '乗組員は、揮発性液体輸送後に実施するﾀﾝｸ洗浄について、関連する危険性を認識しているか？', 'Are personnel aware of the hazards associated with tank cleaning after the carriage of volatile products?', 67);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.68P', '乗組員は、液体のﾀﾝｸへの自由落下を回避する必要性を認識しているか？', 'Are personnel aware of the need to avoid the free fall of liquid into tanks?', 68);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.69P', 'ﾏﾆﾎｰﾙﾄﾞは良好な状態であるか？', 'Are the manifolds in good order?', 69);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.70P', '両舷のﾏﾆﾎｰﾙﾄﾞﾊﾞﾙﾌﾞの外側に圧力計が設置されており、それらは良好な状態であるか？', 'Are manifold pressure gauges fitted outboard of the manifold valves on both sides of the vessel and are they in good order?', 70);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.71P', 'ﾏﾆﾎｰﾙﾄﾞのﾌﾞﾗﾝｸ板は、配管と同等級（材質・耐圧・厚さ）であるか？', 'Are manifold blank flanges of an equivalent rating to that of the manifold pipelines?', 71);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.72P', 'ﾍﾞｰﾊﾟｰﾘﾀｰﾝ用のﾏﾆﾎｰﾙﾄﾞが設置されている場合、それらは良好な状態であるか？', 'If the vessel is fitted with vapour return manifolds, are they in good order?', 72);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.73P', 'ﾍﾞｰﾊﾟｰﾘﾀｰﾝﾏﾆﾎｰﾙﾄﾞがSBM係留用に設計されている場合、要求を満足しているか？', 'If the vapour return manifolds are designed for use at single buoy moorings, do they comply with requirements?', 73);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.74P', '船内の配管は、ｶｰｺﾞ・燃料・ﾊﾞﾗｽﾄを相互に連結するような、違法なものはないか？', 'Does the vessel’s piping system appear to be free of unauthorised inter-connections between cargo, bunker and ballast systems?', 74);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.75P', 'ﾎﾟﾝﾌﾟﾙｰﾑがある船舶について、機器類・ﾊﾞﾙﾌﾞ・ﾊﾞﾙﾌﾞｸﾞﾗﾝﾄﾞ・計器類から顕著な漏洩はないか？', 'On vessels with pump rooms, are they free of evidence of significant leaks from machinery, pipework, valve glands and instrumentation?', 75);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.76P', '隔壁ｼｰﾙはｶﾞｽ漏れがなく、必要ならば、十分に潤滑されているか？', 'Are bulkhead seals gas tight and, if required, well lubricated? ', 76);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.77P', 'ﾎﾟﾝﾌﾟﾙｰﾑのｶﾞｽ検知装置は、良好な状態であり、定期的にﾁｪｯｸされているか？', 'Is the cargo pump room gas monitoring system in good order and regularly checked?', 77);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.78P', 'ﾎﾟﾝﾌﾟﾙｰﾑは清浄で整頓され可燃物も置かれておらず、ﾋﾞﾙｼﾞにはｶｰｺﾞが含まれていないか？', 'Are pumprooms clean, tidy and free of combustible material and are the bilges free of Cargo Product?', 78);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.79P', 'ﾎﾟﾝﾌﾟﾙｰﾑの照明の明るさは適切であるか？', 'Is the level of lighting in the pump room adequate?', 79);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.80P', '保有するｶｰｺﾞﾎｰｽを使用している場合、設計使用圧力で圧力ﾃｽﾄが毎年行なわれており、良好な状態であるか？又、全てのﾎｰｽについて、ﾃｽﾄ・検査の記録が維持されているか？', 'If the vessel uses its own cargo hoses, are they in good order, pressure tested annually to their design working pressure and is a record of all hose tests and inspections maintained on board?', 80);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.81P', '全てのｸﾚｰﾝ・吊り上げ装置は、適切に表示され、定期的なﾃｽﾄ・検査が実施されているか？', 'Are all cargo cranes and other lifting equipment properly marked and has periodical testing and inspection been carried out?', 81);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.82P', '吊り上げ装置用のｳｨﾝﾁは良好な状態であるか？', 'Are winches associated with lifting equipment in good order?', 82);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.83P', 'ﾏﾆﾎｰﾙﾄﾞの中央部に一台のみのｸﾚｰﾝを装備している船舶は、油圧ﾎｰｽの予備をﾌﾙｾｯﾄで一式所持しているか？', 'If the ship has a single centerline mounted crane at the manifold, does it carry a full set of spare hydraulic hoses for the crane?', 83);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.84P', '会社のSTS作業手順書が用意されているか？', 'Are operator’s procedures provided for ship to ship operations?', 84);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.85P', '十分なｸﾛｰｽﾞﾄﾞ型のﾌｪｱﾘｰﾀﾞｰ・ﾋﾞｯﾄが装備されているか？', 'Are sufficient closed fairleads and mooring bitts provided?', 85);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.86P', 'STSﾁｪｯｸﾘｽﾄが完成されているか？', 'Are ship-to-ship transfer checklists completed?', 86);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.87P', '検船時にSTS作業が行なわれている場合、OCIMF/ICSのSTS移送指針に沿って実施されているか？', 'If a ship-to-ship transfer was in progress during the inspection, was it conducted in accordance with the recommendations of the OCIMF/ICS STS Transfer Guide?', 87);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.88P', '会社の手順書が用意されているか？又、”Wet"と”Dry"の切替え記録が維持されているか？', 'Are operator’s procedures provided and are records maintained for changing between the wet and dry modes?', 88);
