INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.93P', '前部ｽﾍﾟｰｽのﾋﾞﾙｼﾞﾎﾟﾝﾋﾟﾝｸﾞｼｽﾃﾑは良好な状態であるか？', 'Are bilge pumping systems for forward spaces in good order? ', 93);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.94P', '前部ｽﾍﾟｰｽ及びﾎｰﾙﾄﾞのﾋﾞﾙｼﾞｱﾗｰﾑが設置されているか？', 'Is the vessel equipped with bilge alarms in the forward spaces and holds?', 94);
