INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.97P', 'DP CAPなどの再訓練ｺｰｽにDPは参加しているか？', 'Do DP personnel undergo assessed refresher training e.g. DP CAP?', 97);
