INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.117P', '船舶は、適切なDP作業ﾏﾆｭｱﾙを船上に用意しているか?', 'Does the vessel have a comprehensive DP operating manual on board? ', 117);
