INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.119P', 'DP作業に先立って、船橋・機関室・電気ｼｽﾃﾑを網羅したﾁｪｯｸﾘｽﾄが用意されているか?', 'Are checklists in place to cover bridge, engine room and electrical systems prior to DP operations?', 119);
