INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.158P', 'STL室にODMが装備されている場合、良態であるか？', 'If an oil discharge monitor is fitted in the STL room, is it in good order?', 158);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (8, '8.159P', 'BLS及び/或いはSTLｽﾍﾟｰｽのﾄﾞﾚｰﾆﾝｸﾞｼｽﾃﾑが装備されているか？', 'Is the vessel equipped with an appropriate system for draining the BLS and/or STL spaces?', 159);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.1C', '安全荷役に関する管理会社の基本方針・ｶﾞｲﾀﾞﾝｽ・手順書が用意されているか？', 'Is the vessel provided with company policy statements, instructions and procedures with regard to safe cargo operations?', 1);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.2C', '最大積荷ﾚｰﾄ及び同ﾍﾞﾝﾄ通風容量の情報が用意されているか？', 'Is information readily available on maximum loading rates and venting capacities?', 2);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.3C', 'ｶｰｺﾞ配管・ｲﾅｰﾄｶﾞｽ・ﾍﾞﾝﾄ装置などを表示した最新で容易に理解可能な配管図若しくは操作台が荷役制御室に備えられているか？', 'Are legible and up to date pipeline and/or mimic diagrams of cargo, inert gas and venting systems available in the cargo control area?', 3);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.4C', 'P&Aﾏﾆｭｱﾙ（機器操作手順と設備ﾏﾆｭｱﾙ）が用意されているか？', 'Is there a Procedures and Arrangements Manual available? ', 4);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.5C', '貨物記録簿が正しく記載され、更新されているか？', 'Is the Cargo Record Book correctly completed and up to date?', 5);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.6C', '引火性・毒性製品積載後、ｹﾐｶﾙや溶剤を使用したり、ｶﾞｽﾌﾘｰやｶｰｺﾞﾀﾝｸをｽﾁｰﾐﾝｸﾞするためのﾀﾝｸｸﾘｰﾆﾝｸﾞｶﾞｲﾄﾞﾗｲﾝおよび手順書が用意されているか？', 'Are there guidelines and procedures for tank cleaning after flammable and toxic products, using chemicals and solvents, gas freeing and for steaming cargo tanks?', 6);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.7C', '現在作業中の荷役について、復元性の計算（該当する場合は強度計算も含む）を行なっているか？', 'Have stability calculations and where applicable stress calculations, been performed for the current cargo operation?', 7);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.8C', '荷役計画には、強度・復元性情報が含まれており、当直航海士は制限事項を理解しているか？', 'Is the stress and stability information included with the cargo plan and are any limitations understood by the cargo watch officers?', 8);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.9C', '損傷時復元性資料を備えているか？', 'Are damage stability guidelines available?', 9);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.10C', '船長は、損傷時復元性資料における最悪の損傷時復元性について認識しているか？', 'Is the master aware of the worst damage stability condition in the stability book?', 10);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.11C', '本船には、非損傷時復元性に関する固有の問題はないか？', 'Is the vessel free of inherent intact stability problem?', 11);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.12C', '積荷計算用のｺﾝﾋﾟｭｰﾀ又はﾌﾟﾛｸﾞﾗﾑが使用されている場合、船級の承認を受けているか？', 'If a loading computer or programme is in use, is it class approved?', 12);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.13C', 'ｺﾝﾋﾟｭｰﾀはその精度について定期的にﾃｽﾄされていることを示す記録があるか？', 'Are there records indicate that the operational accuracy of the load computer is tested regularly?', 13);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.14C', '該当する場合、縦強度は常時設計値内に維持されているか？', 'Are longitudinal stresses, where applicable, maintained within design limits throughout?', 14);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.15C', 'ｶｰｺﾞﾀﾝｸ・ﾊﾞﾗｽﾄﾀﾝｸはｽﾛｯｼﾝｸﾞ又は他の制限が無いか？', 'Are cargo and/or ballast tanks free of sloshing or weight restrictions?', 15);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.16C', '該当する場合、航海士は遊動水の危険性やｽﾛｯｼﾝｸﾞによる構造物への損傷を引起こす可能性について理解しているか？', 'Where applicable, are officers aware of the dangers of high free surface effects and of the possibility of structural damage caused by sloshing in cargo tanks?', 16);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.17C', '該当する場合、会社の作業ﾏﾆｭｱﾙは、荷役中不安定な状態になった場合に復元性を回復するための手順を含んでいるか？', 'Do the operator’s operating manuals include procedures for restoring stability in case of unstable conditions developing during cargo operations, where applicable?', 17);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.18C', '積載中のｶｰｺﾞは、適合証書或いは有害液体物質のためのIPPC証書に記載されているか？', 'Are the cargoes being carried listed on the Certificate of Fitness or IPPC for Noxious Liquid Substances in bulk?', 18);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.19C', '全ての職員はｶｰｺﾞｼｽﾃﾑについて理解しているか？', 'Are all officers familiar with the cargo system?', 19);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.20C', '全ての職員は、積載中のｶｰｺﾞやｹﾐｶﾙ類一般の輸送上の要求事項を理解しているか？', 'Are all officers familiar with the carriage requirements for the cargoes on board and chemicals in general?', 20);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.21C', '不案内のｹﾐｶﾙ製品を輸送する場合、安全及び取り扱い手順について検討する手順が定められているか？', 'When an unfamiliar chemical is to be carried, is there a procedure to review the safety aspects and handling procedures?', 21);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.22C', '貨物適合表が用意されているか?', 'Is a cargo compatibility chart available?', 22);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.23C', 'ｶｰｺﾞ・ﾊﾞﾗｽﾄについて、詳細な移送順序を示す荷役計画書が準備されているか？', 'Has a cargo-handling plan been prepared which provides a detailed sequence of cargo and ballast transfer?', 23);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.24C', '荷役計画書は当直職員が理解し、ｻｲﾝしているか？', 'Has the cargo plan been signed by the watch officers to indicate their understanding of it?', 24);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.25C', '荷役中の作業や、記録されている作業は、荷役計画書に従って実施されているか？', 'Are cargo operations being carried out and logged in accordance with the plan?', 25);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.26C', '重合防止剤が必要なｶｰｺﾞの場合、必要な情報が用意されているか？', 'If the cargo is required to be inhibited, is the required information available?', 26);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.27C', '不適正のｶｰｺﾞが混合することによる危険性が考慮されているか？', 'Are the dangers associated with co-mingling non-compatible cargoes considered?', 27);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.28C', '全ての職員は、ｶｰｺﾞを伴う火災・漏洩・汚染に対応する緊急手順について理解しているか？', 'Are all officers aware of the emergency procedures for dealing with leakage, spillage or fire involving the cargo?', 28);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.29C', '船・陸間の会話による意志疎通に問題はないか？', 'Is the verbal communication between the ship and the shore adequate?', 29);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.30C', 'ﾀﾝｸｸﾘｰﾆﾝｸﾞ計画はｸﾘｰﾆﾝｸﾞ作業前に作成されているか？', 'Is a tank cleaning plan established prior to cleaning operations?', 30);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.31C', '引火性・毒性製品の輸送後に行なうﾀﾝｸｸﾘｰﾆﾝｸﾞ及びﾍﾞﾝﾃｨﾝｸﾞに関して、全ての職員はその危険性を理解しているか？', 'Are officers aware of the dangers associated with tank cleaning and venting operations after the carriage of volatile or toxic products?', 31);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.32C', 'ﾃﾞｨｰﾌﾟｳｪﾙﾎﾟﾝﾌﾟが設置されている場合、その内部ｽﾍﾟｰｽや空所を十分にﾊﾟｰｼﾞする手順が用意されているか？', 'Have satisfactory column/cofferdam purging routines been established where deep well pumps are fitted?', 32);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.33C', 'ｶｰｺﾞﾀﾝｸがｽﾃﾝﾚｽ製の場合、腐食防止・酸洗浄の手順が用意されているか？', 'Where cargo tanks are of stainless steel are there procedures for passivation and pickling?', 33);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.34C', 'ｶｰｺﾞﾀﾝｸ隔壁を利用する壁面洗浄ﾃｽﾄの適切な手順書が用意されているか？', 'Are adequate procedures in place for carrying out wall wash tests?', 34);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.35C', 'ｶｰｺﾞｻﾝﾌﾟﾙﾛｯｶｰは、主ｶｰｺﾞ区画に設置されており、良好な状態であるか？', 'Is the cargo sample locker situated within the main cargo area and is it in a good order?', 35);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.36C', 'ｶｰｺﾞｻﾝﾌﾟﾙﾛｯｶｰは、破損を防護するよう適切に作られているか？', 'Is the cargo sample locker suitably constructed to prevent breakages?', 36);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.37C', 'ｶｰｺﾞｻﾝﾌﾟﾙﾛｯｶｰは適切に換気されているか？', 'Is the cargo sample locker adequately ventilated?', 37);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.38C', 'ｶｰｺﾞ・ﾊﾞﾗｽﾄ・ｽﾄﾘｯﾋﾟﾝｸﾞﾎﾟﾝﾌﾟ・ｴﾀﾞｸﾀｰ・関連機器及び制御装置は良好な状態であり、定期的なﾃｽﾄの記録が用意されているか?', 'Are the cargo, ballast and stripping pumps, eductors and their associated instrumentation and controls, in good order and is there recorded evidence of regular testing?', 38);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.39C', 'ｶｰｺﾞ・ﾊﾞﾗｽﾄﾎﾟﾝﾌﾟのﾍﾞｱﾘﾝｸﾞ・ｹｰｼﾝｸﾞ・ﾄﾞﾗｲﾌﾞｼｬﾌﾄのﾊﾞﾙｸﾍｯﾄﾞ貫通部ｸﾞﾗﾝﾄﾞの温度監視装置は良好な状態であり、定期的なﾃｽﾄの記録が用意されているか?', 'Are the cargo and ballast pump bearing, casing and shaft gland temperature monitoring sensors in good order and is there evidence of regular testing?', 39);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.40C', 'ｶｰｺﾞﾗｲﾝ・原油洗浄ﾗｲﾝ・ﾍﾞｰﾊﾟｰﾗｲﾝ・ｲﾅｰﾄｶﾞｽﾗｲﾝは良好な状態であり、定期的なﾃｽﾄの記録が用意されているか?', 'Are the cargo lines, crude oil washing lines, vopour lines and inert gas lines in good order and is there recorded evidence of regular testing?', 40);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.41C', 'ｶｰｺﾞﾎﾟﾝﾌﾟ緊急停止装置は良好な状態であり、定期的なﾃｽﾄの記録が用意されているか?', 'Is the cargo pump emergency shut down system in good order and is there recorded evidence of regular testing?', 41);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.42C', 'ｶｰｺﾞ・ﾊﾞﾗｽﾄ装置関係ﾊﾞﾙﾌﾞ類は良好な状態であり、定期的なﾃｽﾄの記録が用意されているか?', 'Are the cargo and ballast system valves in good order and is there recorded evidence of regular testing?', 42);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.43C', 'ｶｰｺﾞ装置の液面計・ﾍﾞｰﾊﾟｰﾛｯｸ装置・UTI計測機器類は良好な状態であり、定期的なﾃｽﾄの記録が用意されているか?', 'Are the cargo system ullage gauges, vapour locks and UTI tapes in good order and is there recorded evidence of regular testing?', 43);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.44C', '遠隔・ﾛｰｶﾙの、温度・圧力ｾﾝｻｰとｹﾞｰｼﾞ類は良好な状態であり、定期的なﾃｽﾄの記録が用意されているか?', 'Are the remote and local temperature and pressure sensors and gauges in good order and is there recorded evidence of regular testing?', 44);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.45C', 'ｶｰｺﾞﾀﾝｸ高位液面警報とｵｰﾊﾞｰﾌﾛｰ防止ｱﾗｰﾑは良好な状態であり、定期的なﾃｽﾄの記録が用意されているか?', 'Are the cargo tank high level and overflow alarms in good order and is there recorded evidence of regular testing?', 45);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.46C', '配管のﾄﾞﾚﾝ管や突き出た配管類は、ﾊﾞﾙﾌﾞが閉められ、ｷｬｯﾌﾟがされており、良好な状態であるか？　', 'Are pipeline drains and stub pieces valved and capped and in good order?', 46);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.47C', 'ｶｰｺﾞﾗｲﾝのﾄﾞﾚﾝ管は、残液を除去するために適した位置に配置されているか？', 'Are cargo line drains suitably positioned to preclude liquid remaining in the line after draining?', 47);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.48C', 'ｶｰｺﾞﾀﾝｸの高位液面警報は、測定装置・ｵｰﾊﾞｰﾌﾛｰ制御・警報装置から独立しているか？', 'Is the cargo tank high-level alarm system independent of both the gauging devices and the overflow-control alarm system?', 48);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.49C', '温度計・圧力計を含む、主要な計器類の較正の記録が用意されているか？', 'Are there records of the calibration of key cargo instrumentation, including temperature and pressure gauges?', 49);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.50C', '設置されている場合、ｲﾅｰﾄｶﾞｽ装置・貯蔵装置・関連配管は良好な状態であるか？', 'Is the inert gas system and/or storage and associated pipework, where fitted, in good order?', 50);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.51C', 'ｶｰｺﾞﾀﾝｸの加熱装置は良好な状態であるか？', 'Is the general condition of the cargo tank heating system satisfactory?', 51);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.52C', 'ﾍﾞｰﾊﾟｰﾛｯｸ装置が設置されている場合、それらは認知されたｶｰｺﾞ検定機関によって、較正・照明されているか？', 'Are vapour locks, where fitted, calibrated and certified by a recognised cargo inspection organisation?', 52);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.53C', '閉鎖式液面計が設置されていない場合、荷役中の各ﾀﾝｸを同時に測定するに十分な数の持ち運び式ﾃｰﾌﾟを用意しているか？　', 'If fixed tank gauges are not fitted, are sufficient portable tapes provided to simultaneously gauge each tank being worked? ', 53);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.54C', '引火性や毒性の製品を取り扱う場合、ｱﾚｰｼﾞ測定やｻﾝﾌﾟﾘﾝｸﾞを含めて閉鎖荷役が可能であるか？', 'Is the vessel capable of operating in a closed condition if volatile or toxic products are handled, including ullaging and sampling?', 54);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.55C', '引火性・毒性のｶｰｺﾞを荷役している場合、検船時に閉鎖荷役が実施されているか？', 'If the vessel is handling volatile or toxic cargoes, is it operating in a closed condition at the time of the inspection?', 55);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.56C', 'ﾀﾝｸﾊｯﾁ・洗浄ﾎｰﾙ・覗き窓は、油蜜・気密を維持しているか？', 'Do tank hatches, tank cleaning apertures and sighting ports appear to be liquid and gas tight?', 56);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.57C', 'ｶｰｺﾞﾍﾞﾝﾄ装置は良好な状態であるか？', 'Is the cargo venting system in good order?', 57);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.58C', 'ｶｰｺﾞﾍﾞﾝﾄ装置は適切に使用されているか？', 'Is the cargo venting system being operated correctly?', 58);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.59C', 'SOLAS規定のﾍﾞﾝﾄの二次的手段の要求に適合しているか？', 'Are SOLAS secondary venting requirements being complied with? ', 59);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.60C', 'P/Vﾊﾞﾙﾌﾞは良好な状態であるか？又、日常の定期整備の一環として点検・清掃され、整備記録が用意されているか？', 'Are the P/V valves in good order, inspected and cleaned as part of a regular maintenance routine and are there records to support this?　', 60);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.61C', '防炎ｽｸﾘｰﾝは容易に接近・取り外し可能であるか？又、日常の定期整備の一環として点検・清掃され、整備記録が用意されているか？', 'Are flame screens easily accessible and removable, in good order and inspected and cleaned as part of a regular maintenance routine and are there records available?', 61);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.62C', 'ﾍﾞｰﾊﾟｰﾘﾀｰﾝﾗｲﾝを接続して荷役を行っている場合、適切な移送手順が用意されているか？', 'If the vessel is operating with a vapour return line connected, are appropriate transfer procedures in place?', 62);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.63C', '積荷時の、初期最大流量に関する注意事項は守られているか？', 'Are precautions relating to maximum flow rates during initial loading being observed?', 63);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.64C', '要求される静電気緩和時間は守られているか？', 'Are required settling periods being observed?', 64);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.65C', 'ｶｰｺﾞﾀﾝｸにﾍﾞｰﾊﾟｰﾛｯｸ装置があり、測深管が底部まで達していない場合、ｱﾚｰｼﾞ測定或いはｻﾝﾌﾟﾙ採取前の適切な静置時間に関する静電気注意事項が守られているか?', 'Where vapour locks are fitted to cargo tanks that are not fitted with full depth sounding pipes, are static electricity precautions taken to ensure that the appropriate relaxation period elapses prior to ullaging or sampling?', 65);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.66C', '金属ﾃｰﾌﾟ、その他の検尺・ｻﾝﾌﾟﾘﾝｸﾞ機具は、ﾀﾝｸ内部に入れる前に有効なﾎﾞﾝﾃﾞｨﾝｸﾞを確保しているか？', 'Are metal tapes and other gauging or sampling devices effectively bonded before being introduced into tanks?', 66);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.67C', '可搬式ﾀﾝｸ洗浄ﾎｰｽが使用される場合、導通ﾃｽﾄを実施し、結果を記録しているか？', 'If portable tank cleaning hoses are used, are continuity tests carried out and the results recorded?', 67);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.68C', '乗組員は、引火性液体類の輸送後、ﾀﾝｸｸﾘｰﾆﾝｸﾞに関連する危険性を認識しているか？', 'Are personnel aware of the hazards associated with tank cleaning after the carriage of volatile products?', 68);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.69C', '乗組員は、引火性液体類の輸送後、ｶｰｺﾞﾀﾝｸのｽﾁｰﾐﾝｸﾞに関連する危険性を認識しているか？', 'Are personnel aware of the hazards associated with steaming cargo tanks after the carriage of volatile products?', 69);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.70C', '乗組員は、液体の、ﾀﾝｸへの自由落下を回避する必要性を認識しているか？', 'Are personnel aware of the need to avoid the free fall of liquid into tanks?', 70);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.71C', 'ｶｰｺﾞ配管のｼﾞｮｲﾝﾄ部は導通されているか？', 'Are cargo pipe joints bonded?', 71);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.72C', 'ﾏﾆﾎｰﾙﾄﾞ設備は良好な状態であるか？', 'Are cargo manifold arrangements satisfactory?', 72);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.73C', 'ﾏﾆﾎｰﾙﾄﾞ弁の外側に圧力計が設置されており、それらは良好な状態であるか？', 'Are manifold pressure gauges fitted outboard of the manifold valves and are they in good order?', 73);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.74C', 'ﾏﾆﾎｰﾙﾄﾞの圧力計には、ｽﾄｯﾌﾟﾊﾞﾙﾌﾞ又はｺｯｸが装備されているか？', 'Are manifold pressure gauges fitted with valves or cocks? ', 74);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.75C', 'ﾌﾗﾝｼﾞは、全てのﾎﾞﾙﾄﾅｯﾄが締められているか？', 'Are all flange connections fully bolted?', 75);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.76C', 'ﾏﾆﾎｰﾙﾄﾞのﾌﾞﾗﾝｸ板は、配管と同等級（材質・耐圧・厚さ）であるか？', 'Are manifold blank flanges of an equivalent rating to that of the manifold pipelines?', 76);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.77C', 'ﾏﾆﾎｰﾙﾄﾞの、ﾊﾞﾙﾌﾞ・配管は供用されるﾀﾝｸが判るように表示がされているか？', 'Are the manifold valves and lines marked to identify the tank or tanks they serve?', 77);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.78C', 'ﾏﾆﾎｰﾙﾄﾞは、ﾄﾞﾚﾝﾗｲﾝ・ﾊﾟｰｼﾞﾎﾟｲﾝﾄが備えられ、ﾊﾞﾙﾌﾞがしめられ、ｷｬｯﾌﾟがされているか？', 'Are the manifolds fitted with drain lines and purge points and are they valved and capped?', 78);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.79C', 'ｶｰｺﾞ・燃料・ﾊﾞﾗｽﾄを相互に連結するような違法な配管はないか？', 'Is the vessel free of unauthorised inter-connections between cargo, bunker and ballast systems?', 79);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.80C', 'ﾎﾟﾝﾌﾟﾙｰﾑが設置されている船舶： 機器類・ﾊﾟｲﾌﾟ・ﾊﾞﾙﾌﾞｸﾞﾗﾝﾄﾞ・計器類から顕著な漏洩はないか？', 'On vessels with pump rooms, are they free of evidence of significant leaks from machinery, pipework, valve glands and instrumentation?', 80);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.81C', '隔壁ｼｰﾙはｶﾞｽ漏れがなく、要求される場合十分に潤滑されているか？', 'Are bulkhead seals gas tight and, if required, well lubricated?', 81);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.82C', 'ﾎﾟﾝﾌﾟﾙｰﾑのｶﾞｽ検知装置は、良好な状態であり、定期的にﾁｪｯｸされているか？', 'Is the cargo pump room gas monitoring system in good order and regularly checked?', 82);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.83C', 'ﾋﾞﾙｼﾞﾎﾟﾝﾌﾟは良好な状態であり、ﾎﾟﾝﾌﾟﾙｰﾑ外より操作できるか？', 'Is the bilge pump in good order and can it be operated from a position outside the pump room?', 83);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.84C', '吐出圧力計はｶｰｺﾞﾎﾟﾝﾌﾟ設置場所の外側にあり、良好な状態であるか？', 'Are discharge pressure gauges provided outside where a cargo pump room is fitted and are they in good order?', 84);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.85C', 'IBC又はBCHｺｰﾄﾞで要求される保護具類が備えられているか？', 'Is the vessel provided with the protective equipment required by the IBC or BCH Codes?', 85);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.86C', 'IBC又はBCHｺｰﾄﾞで要求される安全装具が備えられているか？', 'Is the vessel provided with the safety equipment required by the IBC or BCH Codes?', 86);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.87C', '安全装具類は正しく保管・配備されているか？', 'Is the safety equipment correctly located?', 87);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.88C', 'IBC又はBCHｺｰﾄﾞにより要求される呼吸具は、専門業者により、毎年検査されその記録が用意されているか？', 'Has the breathing apparatus required by the IBC or BCH Codes examined by an expert agency annually and are records available?', 88);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.89C', '安全装具類は、毎月検査され記録されているか？', 'Is the safety equipment inspected on board monthly and are records available?', 89);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.90C', '要求される場合、全ての乗船している人員に対する非常時脱出用具が備えてあるか？', 'Are emergency escape sets provided for every person on board where required?', 90);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.91C', 'ﾌｨﾙﾀｰ式防毒ﾏｽｸが備えてある場合、適切な維持管理ｼｽﾃﾑが用意されているか？', 'Where filter-type respirators have been provided, is there an adequate system of control in place?', 91);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.92C', '要求される場合、防染ｼｬﾜｰと洗眼器が適切に表示された場所に備えてあるか？', 'Are decontamination showers and an eye-wash, where required, provided in suitably marked locations?', 92);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.93C', '泡消火剤は、本船の輸送貨物に対して適切なものであるか？', 'Is the type of foam compound suitable for the cargoes which the vessel is certified to carry?', 93);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.94C', '本船が所有するｶｰｺﾞﾎｰｽを使用している場合、ｶｰｺﾞﾎｰｽは良好な状態であり、毎年設計使用荷重で圧力ﾃｽﾄが実施されており、全てのﾎｰｽのﾃｽﾄ及び検査の記録が用意されているか?', 'If the vessel uses its own cargo hoses, are they in good order, pressure tested annually to their design working pressure and is a record of all hose tests and inspections on board?', 94);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.95C', '全てのﾃﾞﾘｯｸ・ｸﾚｰﾝ・吊り上げ装置は、適切に表示され、定期的なﾃｽﾄ・検査が実施されているか？', 'Are all cargo derricks, cranes and other lifting equipment properly marked and has periodical testing and inspection been carried out?', 95);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (9, '8.99C', '吊り上げ装置用のｳｨﾝﾁは良好な状態であるか？', 'Are winches associated with lifting equipment in good order?', 96);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.1G', '安全荷役に関する、会社の方針・指示書・手順書が用意されているか？', 'Is the vessel provided with the operator’s policy statements, instructions and procedures with regard to safe cargo operations?', 1);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.2G', '最大積荷ﾚｰﾄに関する情報が用意されているか？', 'Is information readily available on maximum loading rates?', 2);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.3G', 'ｶｰｺﾞの積載制限に関する情報が用意されているか？', 'Is information on cargo loading limitations available?', 3);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.4G', '適用される場合、ｶｰｺﾞﾗｲﾝ・ｲﾅｰﾄｶﾞｽﾗｲﾝ・ﾍﾞﾝﾄ装置などを表示した、最新で見やすい配管図・若しくは模擬配管図が荷役制御区域に用意されているか？', 'Are legible and up to date pipeline and/or mimic diagrams of the cargo, inert gas and venting systems as applicable, available in the cargo control area?', 4);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.5G', '複数のｺｰﾄﾞを持つ貨物を輸送する場合、機器操作手順と設備ﾏﾆｭｱﾙ（P&Aﾏﾆｭｱﾙ）が用意されているか？', 'Is there a Procedures and Arrangements Manual available where dual code cargoes are carried?', 5);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.6G', '貨物記録簿は正しく記載され更新されているか？', 'Is the Cargo Record Book correctly completed and up to date?', 6);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.7G', '現在作業中の荷役について、復元性の計算（該当する場合は強度計算を含む）が実施されているか？', 'Have stability calculations and, where applicable, stress calculations, been performed for the current cargo operation?', 7);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.8G', '強度と復元性に関する情報が荷役計画に含まれているか？又、制限事項は当直航海士に理解されているか？', 'Is the stress and stability information included with the cargo plan and are any limitations understood by the cargo watch officers?', 8);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.9G', '損傷時復元性資料を用意しているか？', 'Are damage stability guidelines available?', 9);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.10G', '船長は、復元性資料における最悪の損傷時復元性について認識しているか？', 'Is the master aware of the worst damage stability condition in the stability book?', 10);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.11G', '本船には、非損傷時復元性に関する固有の問題はないか？', 'Is the vessel free of inherent intact stability problems?', 11);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.12G', '積荷計算用のｺﾝﾋﾟｭｰﾀ又はﾌﾟﾛｸﾞﾗﾑが使用されている場合、船級の承認を受けているか？', 'If a loading computer or programme is in use, is it class approved?', 12);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.13G', 'ｺﾝﾋﾟｭｰﾀはその精度について定期的にﾃｽﾄされていることを示す記録があるか？', 'Are there records indicating that the operational accuracy of the load computer is tested regularly?', 13);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.14G', '該当する場合、縦強度は常時設計値以内に維持されているか？', 'Are longitudinal stresses, where applicable, maintained within design limits throughout?', 14);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.15G', 'ｶｰｺﾞﾀﾝｸ・ﾊﾞﾗｽﾄﾀﾝｸはｽﾛｯｼﾝｸﾞ又は他の重量制限が無いか？ ', 'Are cargo and/or ballast tanks free of sloshing or weight restrictions? ', 15);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.16G', '該当する場合、航海士は遊動水の危険性や、ｽﾛｯｼﾝｸﾞによる構造物への損傷を引起こす可能性について理解しているか？', 'Where applicable, are officers aware of the dangers of high free surface effects and of the possibility of structural damage caused by sloshing in cargo tanks?', 16);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.17G', '該当する場合、会社の作業ﾏﾆｭｱﾙは荷役中不安定な状態になった場合に復元性を回復するための手順を含んでいるか？', 'Do the operator’s operating manuals include procedures for restoring stability in case of unstable conditions developing during cargo operations, where applicable?', 17);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.18G', '積載中のｶｰｺﾞは、貨物適合証書に記載されているか？', 'Are the cargoes being carried included on the Certificate of Fitness?', 18);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.19G', '全ての職員は、ｶｰｺﾞｼｽﾃﾑについて理解しているか？', 'Are all officers familiar with the cargo system?', 19);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.20G', '全ての職員は、積載中の貨物の輸送上の要求事項を理解しているか？', 'Are all officers familiar with the carriage requirements for the cargoes on board?', 20);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.21G', '一等航海士は基準温度の意味を理解しており、現在のｶｰｺﾞについて決定されているか？', 'Is the chief officer familiar with the term ‘reference temperature’ and has it been determined for this cargo?', 21);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.22G', '不案内のｶﾞｽ製品を輸送する場合、安全及び取り扱い手順について検討する手順が定められているか？', 'When an unfamiliar gas cargo is to be carried, is there a procedure to review the safety aspects and handling procedures?', 22);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.23G', '貨物適合表が用意されているか？', 'Is a cargo compatibility chart available?', 23);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.24G', 'ｶｰｺﾞ・ﾊﾞﾗｽﾄについて、詳細な移送順序を示す荷役計画書が準備されているか？', 'Has a cargo handling plan been prepared which provides a detailed sequence of cargo and ballast transfer?', 24);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.25G', '荷役計画書は、当直職員が理解し、ｻｲﾝしているか？', 'Has the cargo plan been signed by the watch officers to indicate their understanding of it?', 25);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.26G', '荷役中の作業や、記録されている作業は、荷役計画書に従って実施されているか？', 'Are cargo operations being carried out and logged in accordance with the plan?', 26);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.27G', '重合防止剤が必要なｶｰｺﾞの場合、必要な情報が用意されているか？', 'If the cargo is required to be inhibited, is the required information available?', 27);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.28G', '全ての職員は、ｶｰｺﾞを伴う火災・漏洩・汚染に対応する緊急手順について理解しているか？', 'Are all officers aware of the emergency procedures for dealing with leakage, spillage or fire involving the cargo?', 28);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.29G', '設置されている場合、職員はｶｰｺﾞﾎﾟﾝﾌﾟ・ﾌﾞｰｽﾀｰﾎﾟﾝﾌﾟ・ｶｰｺﾞヒーターを直列に配置して使用する場合の、基本原則を理解しているか？', 'Do officers understand the principles involved when cargo and booster pumps and cargo heaters, where fitted, are lined up in series?', 29);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.30G', '設置されている場合、ｻﾌﾞﾏｰｼﾞﾄﾞﾀｲﾌﾟの電動ﾎﾟﾝﾌﾟは、ｶﾞｽﾌﾘｰ作業中は給電は隔離されているか？', 'Are submerged electrical cargo pumps, where fitted, isolated from their electrical supply during gas-freeing operations?', 30);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.31G', '船・陸の音声会話による意志疎通に問題はないか？', 'Is the verbal communication between the ship and the shore adequate?', 31);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.32G', '冷蔵物輸送の場合、水和をｺﾝﾄﾛｰﾙする手段が備えられており、凍結抑制剤の在庫が用意されているか？', 'If refrigerated cargoes are carried, is a means of hydrate control provided and is a supply of freezing depressant maintained onboard?', 32);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.33G', 'ｶｰｺﾞ・ﾌﾞｰｽﾀｰ・ﾊﾞﾗｽﾄ・ｽﾄﾘｯﾋﾟﾝｸﾞﾎﾟﾝﾌﾟ・ｴﾀﾞｸﾀｰ・関連機器及び制御装置は、設置されている場合、良好な状態であり、定期的なﾃｽﾄの記録が用意されているか?', 'Are the Cargo, booster, ballast and stripping pumps, eductors and their associated instrumentation and controls, where fitted, in good order and is there evidence of regular testing?', 33);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.34G', 'ｶｰｺﾞﾋｰﾀｰ及び・又はﾍﾞｰﾊﾟﾗｲｻﾞｰは、設置されている場合、良好な状態であり、定期的なﾃｽﾄの記録が用意されているか?　（LPG船のみ？）', 'Are the Cargo heater and/or vaporiser, where fitted, in good order and is there evidence of regular testing?', 34);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.35G', 'ｶｰｺﾞﾍﾞｰﾊﾟｰ・ｲﾅｰﾄｶﾞｽﾗｲﾝは、良好な状態であり、定期的なﾃｽﾄの記録が用意されているか?', 'Are the Cargo, vapour and inert gas lines in good order? ', 35);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.36G', 'ｶｰｺﾞﾎﾟﾝﾌﾟ緊急停止装置は良好な状態であり、定期的なﾃｽﾄの記録が用意されているか?、', 'Is the Cargo pump emergency shutdown system in good order and is there evidence of regular testing?', 36);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.37G', 'ｶｰｺﾞ･ﾊﾞﾗｽﾄｼｽﾃﾑ関係のﾊﾞﾙﾌﾞは良好な状態であり、定期的なﾃｽﾄの記録が用意されているか?、', 'Are the cargo and ballast system valves in good order and is there evidence of regular testing?', 37);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.38G', 'ｶｰｺﾞﾀﾝｸｼｽﾃﾑの圧力・温度・液面計は良好な状態であり、定期的なﾃｽﾄの記録が用意されているか?', 'Are the Cargo system tank pressure, temperature, and level gauges in good order and is there evidence of regular testing?', 38);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.39G', '遠隔・ﾛｰｶﾙの温度計・圧力計・ｹﾞｰｼﾞ類は良好な状態であり、定期的なﾃｽﾄの記録が用意されているか?', 'Are the Remote and local temperature and pressure sensors and gauges, in good order and is there evidence of regular testing?', 39);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.40G', 'ｶｰｺﾞﾀﾝｸ高位液面警報とｵｰﾊﾞｰﾌﾛｰ防止ｱﾗｰﾑは良好な状態であり、定期的なﾃｽﾄの記録が用意されているか? ', 'Are the Cargo tank high level and overflow alarms in good order and is there evidence of regular testing? ', 40);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.41G', 'ﾃﾞｨｰﾌﾟｳｪﾙﾎﾟﾝﾌﾟ及びﾌﾞｰｽﾀｰﾎﾟﾝﾌﾟのﾒｶﾆｶﾙｼｰﾙに漏洩はないか？', 'Are pump and booster pump mechanical seals free of oil leaks?', 41);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.42G', '緊急時の揚荷方法が用意されているか？', 'Is an emergency discharge method available?', 42);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.43G', 'ﾀﾝｸﾄﾞｰﾑ及び関連装備は良好な状態で、腐食はないか？', 'Are tank domes and associated fittings in good order and free from corrosion?', 43);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.44G', 'ｶｰｺﾞｼｽﾃﾑは、ﾀﾝｸﾄﾞｰﾑ上の設備も含めて漏洩はないか？', 'Is the cargo system, including fittings on the tank domes, free of leaks?', 44);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.45G', '液ﾗｲﾝ及びﾍﾞｰﾊﾟｰﾗｲﾝともにｻﾝﾌﾟﾙﾗｲﾝがあり、ﾊﾞﾙﾌﾞｷｬｯﾌﾟが備えてあるか？', 'Are sample lines provided for both liquid and vapour and are they valved and capped?', 45);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.46G', '低温配管は船体構造物から適切に防熱されているか？', 'Is low temperature pipework adequately insulated from the hull structure?', 46);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.47G', 'ｶｰｺﾞ・ﾍﾞｰﾊﾟｰﾗｲﾝが防熱されている場合、良好な状態であるか？', 'If any cargo or vapour lines are insulated, is the insulation in good order?', 47);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.48G', 'ｶｰｺﾞ・ﾍﾞｰﾊﾟｰﾗｲﾝが構造物から隔離されている場合、各ｼﾞｮｲﾝﾄは電気的にｱｰｽされているか？', 'Where cargo or vapour lines are isolated from the structure, are joints electrically bonded?', 48);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.49G', 'ｶｰｺﾞ・ﾍﾞｰﾊﾟｰﾗｲﾝの伸縮装置は良好な状態であるか？', 'Are cargo and vapour line expansion arrangements in good order?', 49);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.50G', '液ﾗｲﾝ及びﾍﾞｰﾊﾟｰﾗｲﾝ共にｸﾗﾝﾌﾟで固定されていないか？', 'Are liquid and vapour lines free to move inside their clamps?', 50);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.51G', '配管のﾄﾞﾚﾝﾗｲﾝや突き出た配管類は、ﾊﾞﾙﾌﾞが閉められｷｬｯﾌﾟがされており、良好な状態であるか？', 'Are pipeline drains and stub pieces valved and capped and in good order?', 51);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.52G', 'ｶｰｺﾞﾗｲﾝとﾚﾘｰﾌﾊﾞﾙﾌﾞは良好な状態であるか？', 'Are cargo line and system relief valves in good order?', 52);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.53G', 'ｶｰｺﾞﾗｲﾝには、ねじ込み継ぎ手が使用されていないか？', 'Are cargo pipelines free of screwed-in connections?', 53);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.54G', 'ｶｰｺﾞﾀﾝｸ高位液面警報は、測定装置やｵｰﾊﾞｰﾌﾛｰ制御警報装置から独立しているか？', 'Is the cargo tank high level alarm system independent of both the gauging devices and the overflow-control alarm system?', 54);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.55G', '温度計・圧力計を含む主要な計器類の較正の記録が用意されているか？', 'Are there records of the calibration of key cargo instrumentation, including temperature and pressure gauges?', 55);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.56G', 'ｽﾘｯﾌﾟﾁｭｰﾌﾞが設置されている場合、緊急用であるか？', 'If slip tubes are fitted, are they for use only in emergencies?', 56);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.57G', '積荷・揚荷中に高位液面警報装置は作動しているか？', 'Is the high level alarm system operated during both loading and discharging operations?', 57);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.58G', 'ｶｰｺﾞﾀﾝｸには独立した高位液面警報が備えてあるか？又、IGC適用船では、更に、独立した高位液面遮断装置（ｵｰﾊﾞｰﾌﾛｰ制御）が備えてあるか？', 'Are the cargo tank high level alarms independent of the gauging system and in the case of IGC vessels, also independent of the high level shut-down (overflow control) system?', 58);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.59G', '高位液面・遮断装置がｽｲｯﾁ操作で不作動に出来る場合、どのような状況で、又、誰が行なうのか詳細に定めた手順書が用意されているか？', 'If the high level and/or shut-down systems can be overridden by a key switch, is there a written procedure detailing under what circumstances and by whom the system may be overridden?', 59);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.60G', '装備されている場合、ｶｰｺﾞﾀﾝｸ加熱装置は良好な状態であるか？', 'Is the cargo tank heating system, where fitted, operational? ', 60);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.61G', '貨物の再液化装置・関連装置・機器類は良好な状態であるか？', 'Is the cargo conditioning (reliquefaction) plant and associated machinery and instrumentation in good order?', 61);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.62G', 'ｺﾝﾌﾟﾚｯｻｰﾙｰﾑ及びﾓｰﾀｰﾙｰﾑは清浄であり、可燃物が置かれていないか？', 'Are the compressor and motor rooms clean and free of combustible material?', 62);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.63G', 'ｺﾝﾌﾟﾚｯｻｰﾙｰﾑとﾓｰﾀｰﾙｰﾑ間の隔壁ｼｰﾙは、気密であり充分に潤滑されているか？', 'Are the bulkhead seals between the compressor room and the motor room gas tight and well lubricated?', 63);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.64G', 'ｺﾝﾌﾟﾚｯｻｰﾙｰﾑはｶﾞｽの漏洩が無いか？', 'Is the compressor room free of gas leaks?', 64);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.65G', 'ｺﾝﾌﾟﾚｯｻｰﾙｰﾑは十分に照明されており、電気器具類はｶﾞｽ危険区域での使用に適したものであり、良好な状態であるか？', 'Is the compressor room well lit and are electrical fittings suitable for use in gas-hazardous areas and in good order?　', 65);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.66G', 'ｺﾝﾌﾟﾚｯｻｰﾙｰﾑの換気装置は（ﾓｰﾀｰﾙｰﾑに対して）負圧を維持しているか？', 'Is the compressor room ventilation system maintaining negative pressure?', 66);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.67G', 'ﾓｰﾀｰﾙｰﾑの換気ｼｽﾃﾑは正圧を維持しており、作動良好であるか？', 'Is the motor room ventilation system maintaining positive pressure and operating satisfactorily?', 67);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.68G', 'ﾓｰﾀｰﾙｰﾑへのｱｸｾｽ部がｶﾞｽ危険区域内にある場合、両側ﾄﾞｱの同時開放を警告する適切なｱﾗｰﾑを備えたｴｱｰﾛｯｸが設置されているか？', 'If the motor room access is located in a gas-hazardous area, is it provided with an air-lock suitably alarmed to warn of both doors being opened at the same time?', 68);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.69G', 'ｴｱｰﾛｯｸと警報装置は良好な状態であるか？', 'Are airlocks and alarms in good order?', 69);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.70G', 'ｴｱｰﾛｯｸの圧力が下がった場合、停止装置は正しく作動するか？', 'If pressure in the air-lock is lost, will the shutdown system operate correctly?', 70);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.71G', 'ｶﾞｽ検知装置は良好な状態であるか？', 'Is the gas detection equipment in good order?', 71);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.72G', '固定式ｶﾞｽ検知装置のｻﾝﾌﾟﾙ採取位置は、輸送されるｶｰｺﾞの性状に応じた適切な高さにあるか？', 'Are fixed gas detector sample points fitted at the appropriate level for the cargo being carried?', 72);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.73G', 'ｺﾝﾌﾟﾚｯｻｰは、酸化ﾌﾟﾛﾋﾟﾚﾝ輸送時は（ﾌﾞﾗﾝｸ板又はｽﾌﾟｰﾙﾋﾟｰｽの取り外しにより）、貨物から隔離されているか？', 'Are cargo compressors isolated from the cargo when carrying Propylene Oxide?', 73);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.74G', '設置されている場合、ﾎﾞｲﾄﾞｽﾍﾟｰｽのｼｰﾙは良好な状態であるか？', 'Are void space seals, where fitted, in good order?', 74);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.75G', 'ﾎﾞｲﾄﾞｽﾍﾟｰｽの雰囲気管理は良好な状態であるか？', 'Is the environmental control of void spaces satisfactory?', 75);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.76G', '設置されている場合、貨物ﾀﾝｸの防熱は良好な状態であるか？', 'Is cargo tank insulation, where fitted, reported to be in good condition?', 76);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.77G', '設置されている場合、ﾎﾞｲﾄﾞｽﾍﾟｰｽの安全弁は良好な状態であるか？', 'Are relief valves for void spaces, where fitted, in good order? ', 77);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.78G', 'ｲﾝﾀｰﾊﾞﾘｱｰｽﾍﾟｰｽの酸素・炭化水素ｶﾞｽ濃度は定期的に測定・記録されているか？', 'Is the oxygen and hydrocarbon content of the interbarrier spaces regularly monitored and the results recorded?', 78);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.79G', 'ﾎｰﾙﾄﾞｽﾍﾟｰｽ・一次・二次防壁の安全弁は良好な状態であるか？', 'Are the relief valves for the hold spaces and primary and secondary barriers in good order?', 79);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.80G', '設置されている場合、貨物ﾀﾝｸの防熱は良好な状態であるか？', 'Is cargo tank insulation, where fitted, reported to be in good order?', 80);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.81G', 'ｲﾝﾀｰﾊﾞﾘｱｰｽﾍﾟｰｽへの水分浸入をｻﾝﾌﾟﾘﾝｸﾞする手段があり、ﾁｪｯｸ結果が記録されているか？', 'Is there a means to sample for ingress of water into the interbarrier spaces provided and are checks being recorded?', 81);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.82G', '設備されている場合、ｲﾅｰﾄｶﾞｽ装置又は貯蔵装置・関連配管は良好な状態であるか？', 'Is the inert gas system and/or storage and associated pipework, where fitted, in good order?', 82);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.83G', 'ｲﾅｰﾄｶﾞｽ装置への、ｶｰｺﾞﾍﾞｰﾊﾟｰの逆流防止のための適切な設備があるか？', 'Are suitable arrangements provided to prevent the backflow of cargo vapour into the inert gas system?', 83);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.84G', '安全ﾘﾘｰﾌﾊﾞﾙﾌﾞはﾃｽﾄされており、ﾃｽﾄ証明書が備えられているか？又、職員は設定圧力を理解しているか？', 'Have the safety relief valves been tested, are the test certificates onboard and are officers aware of their settings?', 84);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.85G', '安全ﾘﾘｰﾌﾊﾞﾙﾌﾞの設定圧力変更のための適切な手順が用意されているか？', 'Are there adequate procedures for the changing of the relief valve set pressure?', 85);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.86G', 'ｶｰｺﾞﾀﾝｸの安全ﾘﾘｰﾌﾊﾞﾙﾌﾞの設定が変更できる場合、輸送ｶｰｺﾞに対して適切に設定され、現在の設定値がｶｰｺﾞ制御室（位置）と当該ﾘﾘｰﾌﾊﾞﾙﾌﾞに明瞭に表示されているか？', 'If the cargo tank safety relief valve settings can be altered, are the appropriate settings being used for the cargo carried and are the current settings prominently displayed in the cargo control position and at the valves?', 86);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.87G', 'ｶｰｺﾞﾍﾞﾝﾄｼｽﾃﾑは良好な状態であるか？', 'Is the cargo vent system in good order?', 87);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.88G', 'ﾍﾞﾝﾄ出口の防護物又はﾌﾚｰﾑｽｸﾘｰﾝが備えてあり、定期的な点検記録が用意されているか？', 'Are vent outlet protective or flame screens fitted as required and are there records of their regular inspection?', 88);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.89G', '液体検知ｾﾝｻｰが液圧ﾘﾘｰﾌﾊﾞﾙﾌﾞ集積ﾀﾝｸにあるか？ない場合は、ﾍﾞﾝﾄﾎﾟｽﾄに設置されているか？', 'Is there a liquid sensor in the liquid pressure relief valve collecting tank or, if not fitted, in the vent mast?', 89);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.90G', '設置されている場合、ﾍﾞﾝﾄﾎﾟｽﾄの消火装置は良好な状態で作動良好あるか？', 'Are fire extinguishing systems on the vent masts, where fitted, in good order and operational?', 90);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.91G', 'ESD装置は良好な状態であるか？', 'Is the emergency shut down (ESD) system fully operational?', 91);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.92G', '乗組員はESD装置の要求事項を理解しているか？', 'Are personnel aware of the requirements for the ESD system?', 92);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.93G', 'ESDが手動で操作される場合、少なくとも遠隔操作場所は2ヶ所に装備されているか？', 'Are there at least two remote positions where the ESD system can be manually activated?', 93);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.94G', 'ESD装置はﾌｪｲﾙｸﾛｰｽﾞﾄﾞとなっているか？', 'Is the ESD system designed to fail-closed?', 94);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.95G', 'ESD装置は貨物移送前にﾃｽﾄされ記録されているか？', 'Is the ESD system tested prior to cargo transfer and are records maintained?', 95);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.96G', '全てのﾏﾆﾎｰﾙﾄﾞﾊﾞﾙﾌﾞやﾀﾝｸﾌｨﾘﾝｸﾞﾊﾞﾙﾌﾞは、それらがESD装置の一部である場合、30秒以内に閉止するようﾃｽﾄ・調整されているか？', 'Are all manifold valves and tank filling valves, if they form part of the emergency shutdown system, tested and timed to close within 30 seconds?', 96);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.97G', '各ﾄﾞｰﾑ上・ﾏﾆﾎｰﾙﾄﾞ付近にESD可溶ﾌﾟﾗｸﾞが設置されており、良好な状態であるか？', 'Are fusible plugs fitted on the liquid domes and in the vicinity of the manifolds and are they in good order?', 97);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.98G', 'ｶｰｺﾞ・ﾍﾞｰﾊﾟｰﾏﾆﾎｰﾙﾄﾞ設備は良好な状態であるか？', 'Are cargo and vapour manifold arrangements satisfactory?', 98);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.99G', 'ｶｰｺﾞﾏﾆﾎｰﾙﾄﾞの区域には、ｱｰﾑの自動離脱を妨げる障害物はないか？', 'Is the manifold area clear of obstructions which could interfere with the automatic release of a hard arm?', 99);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.100G', 'ｶｰｺﾞﾏﾆﾎｰﾙﾄﾞは、ｱｰﾑ・ﾎｰｽの着脱のため安全に近づくことが出来るように配置されているか？', 'Does the manifold arrangement provide for safe access for connection and disconnection of cargo lines?', 100);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.101G', 'ﾏﾆﾎｰﾙﾄﾞﾊﾞﾙﾌﾞの外側に圧力計が設置されており、良好な状態であるか？', 'Are manifold pressure gauges fitted outboard of the manifold valves and are they in good order?', 101);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.102G', '着岸舷と反対側の舷のﾏﾆﾎｰﾙﾄﾞにも圧力計が設置されており、揚荷中のﾏﾆﾎｰﾙﾄﾞﾊﾞﾙﾌﾞの漏洩の有無について定期的にﾁｪｯｸされているか？', 'Are pressure gauges also fitted to the offshore manifolds and regularly checked during the discharge for manifold valve leakage?', 102);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.103G', 'ﾏﾆﾎｰﾙﾄﾞの圧力計にはﾊﾞﾙﾌﾞ・ｺｯｸがついており、接続は偶然のネジの緩みを防ぐようになっているか？', 'Are manifold pressure gauges fitted with valves or cocks and do the connections provide protection against inadvertent unscrewing?', 103);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.104G', 'ﾌﾗﾝｼﾞのｺﾈｸｼｮﾝは全部のﾎﾞﾙﾄﾅｯﾄが締められているか？', 'Are all flange connections fully bolted?', 104);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.105G', 'ﾏﾆﾎｰﾙﾄﾞのﾌﾞﾗﾝｸ板は、配管と同等級（材質・耐圧・厚さ）であるか？', 'Are manifold blank flanges of an equivalent rating to that of the manifold pipelines?', 105);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.106G', 'ﾏﾆﾎｰﾙﾄﾞのﾊﾞﾙﾌﾞ・ﾗｲﾝは、液用及びﾍﾞｰﾊﾟｰ用が判るように明瞭に表示されているか？', 'Are the manifold valves and lines clearly marked as to whether they are liquid or vapour?', 106);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.107G', 'ﾏﾆﾎｰﾙﾄﾞは、ﾄﾞﾚﾝﾗｲﾝ・ﾊﾟｰｼﾞﾎﾟｲﾝﾄが備えられ、ﾊﾞﾙﾌﾞとｷｬｯﾌﾟがされているか？', 'Are the manifolds fitted with drain lines and purge points and are they valved and capped?', 107);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.108G', '設置されている場合、ﾏﾆﾎｰﾙﾄﾞのｽﾄﾚﾅｰはﾊﾞｲﾊﾟｽされていないか？', 'Are manifold strainers, where fitted, not being by-passed? ', 108);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.109G', '液が漏れた場合の液体受けは適切であり、本船で輸送可能な最低温度の貨物について考慮されているか？', 'Are liquid spill arrangements adequate, taking into account the lowest temperature cargoes which the vessel is certified to carry?', 109);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.110G', '荷役に従事する全ての乗組員に、適切な保護具が備えてあるか？', 'Is suitable protective equipment available for all crew members engaged in cargo operations?', 110);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.111G', '完全な2組以上の安全装具が備えてあり、良好な状態であるか？', 'Are there at least two complete sets of safety equipment on board and are they in good order?', 111);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.112G', '貨物容積が5,000m3以上であれば、追加の消防員装具が備えてあるか？', 'If the vessel has a cargo capacity greater than 5,000 m3, is the additional firemen''s outfit carried?', 112);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.113G', '安全装具は正しく保管・配備されているか？', 'Is the safety equipment correctly located?', 113);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.114G', '貨物容積が2,000m3以上で要求される場合、追加の2組の安全装具を備えているか？', 'Where required in vessels of a cargo capacity of 2,000 m3 and over, are the two additional sets of safety equipment on board?', 114);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.115G', '要求される場合、全ての人員に緊急脱出用具が備えてあり、更に、船橋に2組準備されているか？', 'Where required are emergency escape sets provided for all personnel, plus two sets in the wheelhouse?', 115);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.116G', 'IGCにより要求される安全装具類は、専門業者により毎年検査され、その記録が用意されているか？', 'Is the safety equipment required by the IGC Code examined by an expert annually and are records available?', 116);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.117G', '安全装具類は毎月検査・記録されているか？', 'Is the safety equipment inspected on board monthly and are records available?', 117);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.118G', '要求される場合、除洗ｼｬﾜｰと洗眼器が適切に表示された場所に備えられているか？', 'Are decontamination showers and an eye-wash, where required, provided in suitably marked locations?', 118);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.119G', '粉末消火ｼｽﾃﾑは良好な状態であるか？', 'Is the chemical dry powder system in good order?', 119);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.120G', '水噴霧装置は良好な状態であるか？', 'Is the water spray system in good order?', 120);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.121G', '貨物区域のｽﾏｻﾞｰﾘﾝｸﾞ装置は良好な状態であるか？', 'Are the cargo space smothering systems in good order?', 121);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.122G', '本船が所有しているｶｰｺﾞﾎｰｽを使用している場合、それらは良好な状態であり、毎年設計使用荷重でﾃｽﾄされ、ﾃｽﾄ及び検査の記録が船上に用意されているか？', 'If the vessel uses its own cargo hoses are they in good order, pressure tested annually to their design working pressure and is a record of all hose tests and inspections maintained on board?', 122);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.123G', '全てのﾃﾞﾘｯｸ・ｸﾚｰﾝ・吊り上げ装置は、適切に表示され、定期的なﾃｽﾄ・検査が実施されているか？', 'Are all cargo derricks, cranes and other lifting equipment properly marked and has periodical testing and inspection been carried out?', 123);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.124G', '吊り上げ装置用のｳｨﾝﾁは良好な状態であるか？', 'Are winches associated with lifting equipment in good order?', 124);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.125G', '会社のSTS作業手順書が用意されているか？', 'Are operator’s procedures provided for ship-to-ship operations?', 125);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.126G', '十分なｸﾛｰｽﾞﾄﾞ型のﾌｪｱﾘｰﾀﾞｰ・ﾋﾞｯﾄが装備されているか？', 'Are sufficient closed fairleads and mooring bitts provided?', 126);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.127G', 'STSﾁｪｯｸﾘｽﾄが完成されているか？', 'Are ship-to-ship transfer checklists completed?', 127);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (10, '8.128G', '検船時にSTS作業中の場合、OCIMF/ICS/SIGOTTO/CDIのSTS指針（ｶﾞｽ編）の勧告に沿って実施されているか？', 'If a ship-to-ship transfer was in progress during the inspection, was it conducted in accordance with the recommendations of the OCIMF/ICS/SIGOTTO/CDI STS Transfer Guide (Liquefied Gases)?', 128);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.1L', '安全荷役に関する、会社の方針・指示書・手順書が用意されているか？', 'Is the vessel provided with the operator’s policy statements, instructions and procedures with regard to safe cargo operations?', 1);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.2L', '最大積荷ﾚｰﾄに関する情報が用意されているか？', 'Is information readily available on maximum loading rates?', 2);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.3L', 'ｶｰｺﾞの積載制限に関する情報が用意されているか？', 'Is information on cargo loading limitations available?', 3);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.4L', '適用される場合、ｶｰｺﾞﾗｲﾝ・ｲﾅｰﾄｶﾞｽﾗｲﾝ・ﾍﾞﾝﾄ装置などを表示した、最新で見やすい配管図・若しくは模擬配管図が荷役制御区域に用意されているか？', 'Are legible and up to date pipeline and/or mimic diagrams of the cargo, inert gas and venting systems as applicable, available in the cargo control area?', 4);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.5L', '現在作業中の荷役について、復元性の計算（該当する場合は強度計算を含む）が実施されているか？', 'Have stability calculations and, where applicable, stress calculations, been performed for the current cargo operation?', 5);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.6L', '強度と復元性に関する情報が荷役計画に含まれているか？又、制限事項は当直航海士に理解されているか？', 'Is the stress and stability information included with the cargo plan and are any limitations understood by the cargo watch officers?', 6);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.7L', '損傷時復元性資料を用意しているか？', 'Are damage stability guidelines available?', 7);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.8L', '船長は、復元性資料における最悪の損傷時復元性について認識しているか？', 'Is the master aware of the worst damage stability condition in the stability book?', 8);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.9L', '本船には、非損傷時復元性に関する固有の問題はないか？', 'Is the vessel free of inherent intact stability problems?', 9);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.10L', '積荷計算用のｺﾝﾋﾟｭｰﾀ又はﾌﾟﾛｸﾞﾗﾑが使用されている場合、船級の承認を受けているか？', 'If a loading computer or programme is in use, is it class approved?', 10);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.11L', 'ｺﾝﾋﾟｭｰﾀはその精度について定期的にﾃｽﾄされていることを示す記録があるか？', 'Are there records indicating that the operational accuracy of the load computer is tested regularly?', 11);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.12L', '該当する場合、縦強度は常時設計値以内に維持されているか？', 'Are longitudinal stresses, where applicable, maintained within design limits throughout?', 12);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.13L', 'ｶｰｺﾞﾀﾝｸ・ﾊﾞﾗｽﾄﾀﾝｸはｽﾛｯｼﾝｸﾞ又は他の重量制限が無いか？ ', 'Are cargo and/or ballast tanks free of sloshing or weight restrictions? ', 13);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.14L', '該当する場合、航海士は遊動水の危険性や、ｽﾛｯｼﾝｸﾞによる構造物への損傷を引起こす可能性について理解しているか？', 'Where applicable, are officers aware of the dangers of high free surface effects and of the possibility of structural damage caused by sloshing in cargo tanks?', 14);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.15L', '該当する場合、会社の作業ﾏﾆｭｱﾙは荷役中不安定な状態になった場合に復元性を回復するための手順を含んでいるか？', 'Do the operator’s operating manuals include procedures for restoring stability in case of unstable conditions developing during cargo operations, where applicable?', 15);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.16L', '全ての職員は、ｶｰｺﾞｼｽﾃﾑについて理解しているか？', 'Are all officers familiar with the cargo system?', 16);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.17L', '通常の貨物関連作業をｶﾊﾞｰした貨物作業ﾏﾆｭｱﾙを用意しているか?', 'Is a Cargo Operation Manual available that covers all routine cargo operations?', 17);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.18L', '全ての職員は、LNGの輸送上の要求事項を理解しているか？', 'Are all officers familiar with the carriage requirements for LNG?', 18);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.19L', 'ｶｰｺﾞ・ﾊﾞﾗｽﾄについて、詳細な移送順序を示す荷役計画書が準備されているか？', 'Has a cargo handling plan been prepared which provides a detailed sequence of cargo and ballast transfer?', 19);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.20L', '荷役計画書は、当直職員が理解し、ｻｲﾝしているか？', 'Has the cargo plan been signed by the watch officers to indicate their understanding of it?', 20);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.21L', '荷役中の作業や、記録されている作業は、荷役計画書に従って実施されているか？', 'Are cargo operations being carried out and logged in accordance with the plan?', 21);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.22L', '全ての職員は、ｶｰｺﾞを伴う火災・漏洩・汚染に対応する緊急手順について理解しているか？', 'Are all officers aware of the emergency procedures for dealing with leakage, spillage or fire involving the cargo?', 22);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.23L', '設置されている場合、ｻﾌﾞﾏｰｼﾞﾄﾞﾀｲﾌﾟ電動ﾎﾟﾝﾌﾟは、ｶﾞｽﾌﾘｰ作業中、給電は隔離されているか？', 'Are submerged electrical cargo pumps, where fitted, isolated from their electrical supply during gas-freeing operations?', 23);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.24L', '船・陸の音声会話による意志疎通に問題はないか？', 'Is the verbal communication between the ship and the shore adequate?', 24);
