INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.29L', 'ｶｰｺﾞﾀﾝｸｼｽﾃﾑの圧力・温度・液面計は良好な状態であり、定期的なﾃｽﾄの記録が用意されているか?', 'Are the Cargo system tank pressure, temperature, and level gauges in good order and is there evidence of regular testing?', 29);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.30L', '遠隔・ﾛｰｶﾙの温度計・圧力計・ｹﾞｰｼﾞ類は良好な状態であり、定期的なﾃｽﾄの記録が用意されているか?', 'Are the Remote and local temperature and pressure sensors and gauges, in good order and is there evidence of regular testing?', 30);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.31L', '緊急時の揚荷方法が用意されているか？', 'Is an emergency discharge method available?', 31);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.32L', 'ﾀﾝｸﾄﾞｰﾑ及び関連装備は良好な状態で、腐食はないか？', 'Are tank domes and associated fittings in good order and free from corrosion?', 32);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.33L', 'ｶｰｺﾞｼｽﾃﾑは、ﾀﾝｸﾄﾞｰﾑ上の設備も含めて漏洩はないか？', 'Is the cargo system, including fittings on the tank domes, free of leaks?', 33);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.34L', '低温配管は船体構造物から適切に防熱されているか？', 'Is low temperature pipework adequately insulated from the hull structure?', 34);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.35L', 'ｶｰｺﾞ・ﾍﾞｰﾊﾟｰﾗｲﾝが防熱されている場合、良好な状態であるか？', 'If any cargo or vapour lines are insulated, is the insulation in good order?', 35);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.36L', 'ｶｰｺﾞ・ﾍﾞｰﾊﾟｰﾗｲﾝが構造物から隔離されている場合、各ｼﾞｮｲﾝﾄは電気的にｱｰｽされているか？', 'Where cargo or vapour lines are isolated from the structure, are joints electrically bonded?', 36);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.37L', 'ｶｰｺﾞ・ﾍﾞｰﾊﾟｰﾗｲﾝの伸縮装置は良好な状態であるか？', 'Are cargo and vapour line expansion arrangements in good order?', 37);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.38L', '液ﾗｲﾝ及びﾍﾞｰﾊﾟｰﾗｲﾝ共にｸﾗﾝﾌﾟで固定されていないか？', 'Are liquid and vapour lines free to move inside their clamps?', 38);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.39L', 'ｶｰｺﾞﾗｲﾝとﾚﾘｰﾌﾊﾞﾙﾌﾞは良好な状態であるか？', 'Are cargo line and system relief valves in good order?', 39);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.40L', 'ｶｰｺﾞﾗｲﾝには、ねじ込み継ぎ手が使用されていないか？', 'Are cargo pipelines free of screwed-in connections?', 40);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.41L', 'ｶｰｺﾞﾀﾝｸ高位液面警報は、測定装置やｵｰﾊﾞｰﾌﾛｰ制御警報装置から独立しているか？', 'Is the cargo tank high level alarm system independent of both the gauging devices and the overflow-control alarm system?', 41);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.42L', '温度計・圧力計を含む主要な計器類の較正の記録が用意されているか？', 'Are there records of the calibration of key cargo instrumentation, including temperature and pressure gauges?', 42);
