INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.47L', 'ｺﾝﾌﾟﾚｯｻｰﾙｰﾑとﾓｰﾀｰﾙｰﾑ間の隔壁ｼｰﾙは、気密であり有効に作動しているか？', 'Are the bulkhead seals between the, compressor room and the motor room, gas tight and operating effectively?', 47);
