INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.52L', 'ｺﾝﾌﾟﾚｯｻｰﾙｰﾑはｶﾞｽの漏れがないか?', 'Is the compressor room free of gas leaks?', 52);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.53L', 'ｶﾞｽ検知装置は良好な状態であるか？', 'Is the gas detection equipment in good order?', 53);
