INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.70L', 'ｲﾝﾀｰﾊﾞﾘｱｰｽﾍﾟｰｽへの水分浸入をｻﾝﾌﾟﾘﾝｸﾞする手段があり、ﾁｪｯｸ結果が記録されているか？', 'Is there a means to sample for ingress of water into the interbarrier spaces provided and are checks being recorded?', 70);
