INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.76L', 'ﾍﾞﾝﾄ出口の防護物又はﾌﾚｰﾑｽｸﾘｰﾝが備えてあり、定期的な点検記録が用意されているか？', 'Are vent outlet protective or flame screens fitted as required and are there records of their regular inspection?', 76);
