INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.86L', 'ESD装置は貨物移送前にﾃｽﾄされ記録されているか？', 'Is the ESD system tested prior to cargo transfer and are records maintained?', 86);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.87L', '全てのﾏﾆﾎｰﾙﾄﾞﾊﾞﾙﾌﾞやﾀﾝｸﾌｨﾘﾝｸﾞﾊﾞﾙﾌﾞは、それらがESD装置の一部である場合、30秒以内に閉止するようﾃｽﾄ・調整されているか？', 'Are all manifold valves and tank filling valves, if they form part of the emergency shutdown system, tested and timed to close within 30 seconds?', 87);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.88L', '各ﾄﾞｰﾑ上・ﾏﾆﾎｰﾙﾄﾞ付近にESD可溶ﾌﾟﾗｸﾞが設置されており、良好な状態であるか？', 'Are fusible plugs fitted on the liquid domes and in the vicinity of the manifolds and are they in good order?', 88);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.89L', '再液化装置を装備している場合、ESDが作動した際にﾄﾘｯﾌﾟするようになっているか？', 'If the vessel is fitted with a reliquefaction plant, will this be tripped in the event of activation of the ESD?', 89);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.90L', '洋上航行中、二次ﾀﾝｸ圧力管理ｼｽﾃﾑは再液化装置が不稼動となった場合においても、十分なｶﾞｽ量を扱えるようになっているか？　注：　港内の場合は、ボイルオフｶﾞｽが発生した場合は陸側に返すようにする。洋上においては、ﾍﾞﾝﾄで逃がすのを避けるために、ｶﾞｽ燃焼装置で使用するか、他の類似の方法が必要である。', 'Whilst running at sea, is the secondary tank pressure management system sufficient to handle the gas volume in the event of a shut down of the reliquefaction system? ', 90);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.91L', '警報及びESDのﾄﾘｯﾌﾟをｵｰﾊﾞｰﾗｲﾄﾞする権限についての明確なｶﾞｲﾀﾞﾝｽが掲示されているか？', 'Is clear guidance posted as to the authority to override alarms and ESD trips?', 91);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.92L', 'ｶｰｺﾞ・ﾍﾞｰﾊﾟｰﾏﾆﾎｰﾙﾄﾞ設備は良好な状態であるか？', 'Are cargo and vapour manifold arrangements satisfactory?', 92);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.93L', 'ｶｰｺﾞﾏﾆﾎｰﾙﾄﾞの区域には、ｱｰﾑの自動離脱を妨げる障害物はないか？', 'Is the manifold area clear of obstructions which could interfere with the automatic release of a hard arm?', 93);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.94L', 'ｶｰｺﾞﾏﾆﾎｰﾙﾄﾞは、ｱｰﾑ・ﾎｰｽの着脱のため安全に近づくことが出来るように配置されているか？', 'Does the manifold arrangement provide for safe access for connection and disconnection of cargo lines?', 94);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.95L', '貨物作業中、ﾏﾆﾎｰﾙﾄﾞ区域への接近を制限する眼に見える方法を用意しているか?', 'Are visible means provided to restrict access to the manifold area during cargo operations? ', 95);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.96L', 'ﾏﾆﾎｰﾙﾄﾞﾊﾞﾙﾌﾞの外側に圧力計が設置されており、良好な状態であるか？', 'Are manifold pressure gauges fitted outboard of the manifold valves and are they in good order?', 96);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.97L', '沖側のﾏﾆﾎｰﾙﾄﾞは貨物移送中ﾏﾆﾎｰﾙﾄﾞﾊﾞﾙﾌﾞの漏洩の有無について定期的にﾁｪｯｸされているか？', 'Are the offshore manifolds regularly checked during cargo transfer for manifold valve leakage?', 97);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.98L', 'ﾌﾗﾝｼﾞのｺﾈｸｼｮﾝは全部のﾎﾞﾙﾄﾅｯﾄが締められているか？', 'Are all flange connections fully bolted?', 98);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.99L', 'ﾏﾆﾎｰﾙﾄﾞのﾌﾞﾗﾝｸ板は、配管と同等級（材質・耐圧・厚さ）であるか？', 'Are manifold blank flanges of an equivalent rating to that of the manifold pipelines?', 99);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.100L', 'ﾏﾆﾎｰﾙﾄﾞのﾊﾞﾙﾌﾞ・ﾗｲﾝは、液用及びﾍﾞｰﾊﾟｰ用が判るように明瞭に表示されているか？', 'Are the manifold valves and lines clearly marked as to whether they are liquid or vapour?', 100);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.101L', 'ﾏﾆﾎｰﾙﾄﾞは、ﾄﾞﾚﾝﾗｲﾝ・ﾊﾟｰｼﾞﾎﾟｲﾝﾄが備えられ、ﾊﾞﾙﾌﾞとｷｬｯﾌﾟがされているか？', 'Are the manifolds fitted with drain lines and purge points and are they valved and capped?', 101);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.102L', 'ﾏﾆﾎｰﾙﾄﾞのｽﾄﾚﾅｰはﾊﾞｲﾊﾟｽされていないか？', 'Are manifold strainers not being by-passed? ', 102);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.103L', 'LNGが漏れた場合の液体受けは適切であるか？', 'Are LNG spill arrangements adequate?', 103);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.104L', '舷外の水噴霧装置は使用されているか？', 'Is the overboard water spray curtain in use? ', 104);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.105L', 'ﾏﾆﾎｰﾙﾄﾞのﾄﾞﾘｯﾌﾟﾄﾚｲ区域は、支点にかかる荷重に対応したﾃﾞｻﾞｲﾝとなっているか？', 'Is the manifold drip tray area designed to be load bearing? ', 105);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.106L', '液体漏洩及びﾏﾆﾎｰﾙﾄﾞのﾄﾞﾘｯﾌﾟﾄﾚｰは、適切であるか？', 'Are Liquid Spill and Manifold Drip tray arrangements adequately? ', 106);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.107L', 'ｱｰﾑの切り離しの際、乗組員はﾄﾞﾚﾝｺｯｸ経由のｱｰﾑからの液体のﾊﾟｰｼﾞに起因する危険を承知しているか？', 'During the disconnection of the loading arms are the crew aware of the hazards related to the purging of liquid from the arms via the drain cocks?', 107);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.108L', '荷役に従事する全ての乗組員に、適切な保護具が備えてあるか？', 'Is suitable protective equipment available for all crew members engaged in cargo operations?', 108);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.109L', '完全な2組以上の安全装具が備えてあり、良好な状態であるか？', 'Are there at least two complete sets of safety equipment on board and are they in good order?', 109);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.110L', '貨物容積が5,000m3以上であれば、追加の消防員装具が備えてあるか？', 'If the vessel has a cargo capacity greater than 5,000 m3, is the additional firemen''s outfit carried?', 110);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.111L', '安全装具は正しく保管・配備されているか？', 'Is the safety equipment correctly located?', 111);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.112L', 'IGCにより要求される安全装具類は、専門業者により毎年検査され、その記録が用意されているか？', 'Is the safety equipment required by the IGC Code examined by an expert annually and are records available?', 112);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.113L', '安全装具類は毎月検査・記録されているか？', 'Is the safety equipment inspected on board monthly and are records available?', 113);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.114L', '固定式の粉末消火ｼｽﾃﾑは使用できるようになっており、良好な状態であるか？', 'Is the fixed chemical dry powder system ready for use and in good order?', 114);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.115L', '水噴霧装置は良好な状態であるか？', 'Is the water spray system in good order?', 115);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.116L', '貨物区域のｽﾏｻﾞｰﾘﾝｸﾞ装置は良好な状態であるか？', 'Are the cargo space smothering systems in good order?', 116);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.117L', '本船が所有しているｶｰｺﾞﾎｰｽを使用している場合、それらは良好な状態であるか', 'If the vessel uses its own cargo hoses are they in good order?', 117);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.118L', 'ﾎｰｽはIGCｺｰﾄﾞに従って圧力ﾃｽﾄが行われているか？', 'Are the hoses pressure tested in accordance with the requirement of the IGC?', 118);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.119L', 'ﾎｰｽのﾃｽﾄ・検査の記録が用意されているか?', 'Is a record of all hose tests and inspections maintained on board?', 119);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.120L', '全てのﾃﾞﾘｯｸ・ｸﾚｰﾝ・吊り上げ装置は、適切に表示され、定期的なﾃｽﾄ・検査が実施されているか？', 'Are all cargo derricks, cranes and other lifting equipment properly marked and has periodical testing and inspection been carried out?', 120);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.121L', '吊り上げ装置用のｳｨﾝﾁは良好な状態であるか？', 'Are winches associated with lifting equipment in good order?', 121);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.122L', 'LNGの移送に使用する装置は型式承認されているか？', 'Is the equipment used for ship-to-ship transfer of LNG type-approved?', 122);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.123L', '会社のSTS作業手順書が用意されているか？', 'Are operator’s procedures provided for ship-to-ship operations?', 123);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.124L', '十分なｸﾛｰｽﾞﾄﾞ型のﾌｪｱﾘｰﾀﾞｰ・ﾋﾞｯﾄが装備されているか？', 'Are sufficient closed fairleads and mooring bitts provided?', 124);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.125L', 'STSﾁｪｯｸﾘｽﾄが完成されているか？', 'Are ship-to-ship transfer checklists completed?', 125);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (11, '8.126L', '検船時にSTS作業中の場合、OCIMF/ICSのSTS指針（ｶﾞｽ編）の勧告に沿って実施されているか？', 'If a ship-to-ship transfer was in progress during the inspection, was it conducted in accordance with the recommendations of the OCIMF/ICS STS Transfer Guide (Liquefied Gases)?', 126);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.1', '係船ﾛｰﾌﾟ・ﾜｲﾔｰの証明書が用意されているか？', 'Are certificates available for all mooring ropes and wires?', 1);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.2', '全ての係船ﾛｰﾌﾟ及びﾜｲﾔｰのﾃｰﾙﾛｰﾌﾟ（装備している場合）は、OCIMFｶﾞｲﾄﾞﾗｲﾝに適合しているか？', 'Do all mooring ropes and where fitted, mooring wire tails, meet OCIMF guidelines?', 2);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.3', '1個又はそれ以上のﾊﾞｳｽﾄｯﾊﾟｰが装備されている場合、SWLの証明書を所持しているか？', 'If one or more bow stoppers are fitted, is a certificate attesting to the safe working load provided?', 3);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.4', '係船ﾛｰﾌﾟ・ﾜｲﾔｰ・係留設備の、検査・整備の記録が用意されているか？', 'Are there records of the inspection and maintenance of mooring ropes, wires and equipment?', 4);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.5', '係船機のﾌﾞﾚｰｷﾃｽﾄの方針が定められており、ﾃｽﾄ結果が記録されているか？', 'Is there a policy in place for the testing of winch brakes and are the results recorded?', 5);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.6', '係船索は適切に配置され、張り合わせられているか？', 'Are moorings satisfactorily deployed and tended?', 6);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.7', '係留索はﾋﾞｯﾄに係止され、適切に巻かれているか？', 'Are mooring lines secured to bitts and turned up correctly?', 7);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.8', '全てのﾄﾞﾗﾑ巻き係留索は正しい方向に巻き取られ、確実にﾌﾞﾚｰｷで止められ、ｳｨﾝﾁのｸﾗｯﾁｷﾞｱｰは外されているか？', 'Are all powered mooring lines correctly reeled on drums, secured on brakes and winches out of gear?', 8);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.9', 'ｽﾌﾟﾘｯﾄﾄﾞﾗﾑｳｨﾝﾁの場合、全てのﾗｲﾝはﾃﾝｼｮﾝ・ﾄﾞﾗﾑに1層以上巻かれていないか？', 'On split drum winches are all the lines made fast with no more than one layer on each tension side of the drum?', 9);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.10', 'ﾜｲﾔｰのﾃｰﾙﾛｰﾌﾟが使用されている場合、適切な連結具で接続され、正しく取り付けられているか？', 'If mooring tails are fitted to wires, do they have proper connecting links and are they correctly fitted?', 10);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.11', '全ての係留索は、足元の危険を避けるように整然と格納され、作業場所は確保され、妨げるものはないか？', 'Are all mooring lines stowed neatly to minimise tripping hazards and are mooring areas clear and unobstructed?', 11);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.12', '係船ｳｨﾝﾁの基礎部も含め、係船ｳｨﾝﾁは良好な状態であるか？', 'Are mooring winches, including winch foundations in good order?  ', 12);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.13', 'ﾌﾞﾚｰｷﾗｲﾆﾝｸﾞ・ﾄﾞﾗﾑ・ﾋﾟﾝの外観は良好な状態であるか？', 'Do brake linings, drums and pins appear to be in good order?', 13);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.14', 'ｶﾞｽ危険区域に電動係船機が設置されている場合、ﾓｰﾀｰは完全防爆型（Ex’d）であり、絶縁ﾃｽﾄが実施され結果が記録されているか？', 'If mooring winches in a gas hazardous area are electrically powered, are motors Ex ‘d’ rated and have insulation tests carried out and results recorded?', 14);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.15', '係船ﾜｲﾔｰ・ﾛｰﾌﾟ・ﾃｰﾙﾛｰﾌﾟは良好な状態であるか？', 'Are mooring wires, ropes and synthetic tails in good order?', 15);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.16', 'ﾌｪｱﾘｰﾀﾞｰ・ﾛｰﾗｰ類はｸﾞﾘｰｽが施され自由に回転するか？又、ﾋﾞｯﾄ・ﾁｮｯｸ類は摩耗による溝食などが無いか？', 'Are pedestal fairleads, roller fairleads and other rollers well greased and free to turn and are bitts and chocks free of grooving?', 16);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.17', '係船設備にSWLが表示されているか？', 'Is mooring equipment marked with its SWL?', 17);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.18', 'ｳｨﾝﾄﾞﾗｽ・錨･ﾛｯｷﾝｸﾞﾊﾞｰ･錨鎖は良好な状態であり、運転状況も問題ないか？', 'Are windlasses, anchors, locking bars and cables in good order and operating effectively?', 18);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.19', 'ﾛｯｷﾝｸﾞﾊﾞｰをｾｯﾄする着桟時を除き、入港作業中、錨は開放され直ちに使用できる状態であるか？', 'Except whilst alongside, when locking bars should be in place, were the anchors cleared and ready for immediate use during port entry?', 19);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.20', 'ﾋﾞﾀｰｴﾝﾄﾞ（錨鎖の根づけ装置）は、錨鎖庫の外に設置されており、接近を妨げるものはないか？', 'Are bitter end securing arrangements unobstructed and outside the chain locker?', 20);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.21', '錨鎖庫のﾄﾞｱは確実に閉鎖されているか？', 'Are the chain locker doors securely battened down?', 21);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.22', 'ＳＰＭ及び装備されている設備はOCIMFの勧告に従ったものであるか？', 'Is single point mooring (SPM) and associated equipment fitted to OCIMF recommendations?', 22);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.23', 'ＳＰＭ係留用の設備を装備している場合、係留設備に関する指針（MEG　3rd Edition）に含まれている適用可能な推奨措置に合致しているか?', 'If the vessel is equipped for mooring at single point moorings, does it meet the recommendations as applicable, contained in Mooring Equipment Guidelines (3rd Edition)?', 23);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.24', '油圧操作のﾊﾞｳｽﾄｯﾊﾟｰを装備している場合、予期しないﾚﾘｰｽを防止するための安全策が用意されているか？', 'If the vessel is fitted with a hydraulically operated bow stopper, are safeguards provided to prevent its accidental release?', 24);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.25', '非常用曳航設備（ETA）は船首尾共に直ちに使用できるように準備されているか？', 'Are emergency towing arrangements readily available for deployment　at both ends of the vessel? ', 25);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (12, '9.26', '非常用曳航手順書を所持しているか?', 'Does the vessel have on board Emergency Towing Procedures?', 26);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (13, '10.1', '緊急時用のDSC（ﾃﾞｼﾞﾀﾙ選択呼出し）及び衛星通信機器の操作説明書は明瞭に表示されているか？', 'Are instructions for operating the digital selective calling (DSC) and satellite communications equipment in an emergency clearly displayed?', 1);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (13, '10.2', '船舶のｺｰﾙｻｲﾝ及びｲﾝﾏﾙｻｯﾄ船舶局は無線設備に明瞭に表示されているか？', 'Are the vessel’s call sign and Inmarsat ship station identity clearly marked on the radio installation?', 2);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (13, '10.3', '職員は、緊急時の機器操作方法を十分に理解しているか？', 'Can officers demonstrate a satisfactory understanding of how to operate the equipment in an emergency?', 3);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (13, '10.4', '職員は、船舶保安警報装置の機能・操作方法を理解しているか？', 'Are officers aware of the function of the ship security alert system and how it operates? ', 4);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (13, '10.5', '遭難通信担当（船長以外）として、資格ある乗組員が指名されているか？', 'Has a qualified person been designated to handle distress communications?', 5);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (13, '10.6', '通信機器の定期的なﾃｽﾄが要求どおり実施されているか？', 'Are the periodical tests of communications equipment being carried out as required?', 6);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (13, '10.7', '無線日誌は正しく記入されているか？', 'Is the Radio Log being maintained correctly? ', 7);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (13, '10.8', '該当する場合、非常用無線ﾊﾞｯﾃﾘｰの日誌は記入されているか？', 'If applicable, is the emergency radio battery log up to date?', 8);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (13, '10.9', '無線設備の利用確保のため、適切な保守整備計画が用意されているか？', 'Is there a maintenance programme in place to ensure availability of the radio equipment?', 9);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (13, '10.10', '通信設備は良好な状態であるか？', 'Is the communications equipment in good order?', 10);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (13, '10.11', '衛星EPIRBは、正しく設置・準備・表示され、製造者の要求どおり検査されているか？', 'Is the satellite EPIRB fitted, armed and labelled correctly and inspected in accordance with the manufacturer’s requirements?', 11);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (13, '10.12', '無線機の非常用ﾊﾞｯﾃﾘｰは良好な状態であり、完全に充電されているか？', 'Are radio emergency batteries in good order and fully charged?', 12);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (13, '10.13', '無線信号表は最新版で、改補・更新されているか？', 'Are Lists of Radio Signals the latest edition and corrected up to date?', 13);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (13, '10.14', '甲板上で使用する本質安全型のﾄﾗﾝｼｰﾊﾞｰを所持しているか？', 'Is the vessel equipped with sufficient intrinsically safe portable radios for use on deck?', 14);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (13, '10.15', '救助船用の携帯VHF・捜索救助用ﾚｰﾀﾞｰﾄﾗﾝｽﾎﾟﾝﾀﾞｰ(SART）は良好な状態で、充電されているか？', 'Are survival craft portable VHF radios and Search and Rescue Locating Devices in good order and charged?', 15);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.1', '適切な会社の指示書・手順書が十分備えられているか？', 'Is the vessel provided with adequate operator’s instructions and procedures? ', 1);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.2', '当直者（機関士・機関部員）の責務が明確に定められているか？', 'Are the duties of the watch-standing officers and ratings clearly defined?', 2);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.3', 'M0資格を有している場合、その形態で運転されているか？', 'If the machinery space is certified for unmanned operation is it being operated in that mode? ', 3);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.4', '機関室有人運転の場合、十分な数の機関士が乗船しているか？', 'If the machinery space is being operated manned, are there sufficient engineers on board?', 4);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.5', '機関長は自身の機関当直基準を作成しており、当直機関士は当直基準及び夜間命令簿を読み、理解したとしてｻｲﾝをしているか？', 'Has the chief engineer written his own standing orders and have the watch engineers countersigned the standing and night orders as read and understood?', 5);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.6', 'M0運転中、機関室立入に要求される文書化された手順書が用意されており、船上の乗組員はこれらの要求をしっているか？', 'Are there documented procedures that address engine room entry requirements when operating in the UMS mode, and are the personnel onboard aware of these requirements?', 6);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.7', 'ﾃﾞｯﾄﾞﾏﾝｱﾗｰﾑが設置されている場合、それは良好な状態であり、必要に応じて使用されているか？', 'Is the dead man alarm system, where fitted, in good order and used as required?', 7);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.8', '機関日誌は適切に記入されているか？', 'Is the engine room log book adequately maintained?', 8);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.9', '重要機器類の再起動の手順書が用意されているか？', 'Are there procedures to restart critical equipment?', 9);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.10', '会社は、燃料・潤滑油・油圧作動油の（品質）検査ﾌﾟﾛｸﾞﾗﾑを提供しており、結果についてどのようにするか手順を定めているか？', 'Does the operator subscribe to a fuel, lubricating and hydraulic oil testing programme, and is there a procedure in place to take into account the result?', 10);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.11', '詳細な燃料移送に関する説明書が用意されているか？', 'Are detailed bunker transfer instructions available?', 11);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.12', '低硫黄燃料をﾎﾞｲﾗｰに使用することに関し、船舶はSECA/ECA及び地方当局の要求に安全に従うことができるか?', 'Is the vessel able to safely comply with SECA/ECA legislation or other local requirements regarding use of low sulphur fuels in boilers? ', 12);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.13', '使用中の燃料から低硫黄燃料への変更に関することをコントロールする文書化された手引書が用意されているか?', 'Are written instructions provided to control the change from residual to low-sulphur fuels? ', 13);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.14', 'PMSは実行されており、最新のものであるか？', 'Is a planned maintenance system being followed and is it up to date?', 14);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.15', '予備品の適切で最新のｲﾝﾍﾞﾝﾄﾘｰ管理が行なわれているか？', 'Is a comprehensive and up to date inventory of spare parts being maintained?', 15);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.16', '機関士呼出し警報が設置され、それらは良好な状態であり、定期的なﾃｽﾄ記録も用意されているか？', 'Is an engineer’s call alarm fitted and is it in good order and tested regularly and the results recorded?', 16);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.17', '緊急脱出ﾙｰﾄには、有効な標識がつけられ、妨げるものが無く、適切に照明されているか？', 'Are emergency escape routes effectively marked, unobstructed and adequately lit?', 17);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.18', '機関室全域の照明の明るさは適切であるか？', 'Is the level of lighting in all areas of the engine room satisfactory?', 18);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.19', '非常用機器に対する定期的なﾃｽﾄの記録が用意されているか？', 'Do records indicate the regular testing of emergency equipment? ', 19);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.20', '機関室の外側でﾊﾞﾙﾌﾞ操作出きる燃料遮断ｼｽﾃﾑは定期的にﾃｽﾄされており、良好な状態であるか? ', 'Is the fuel system fitted with valves that are capable of being closed from outside the machinery space regularly tested and in good order?', 20);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.21', '換気装置の緊急停止は明瞭に表示され、定期的にﾃｽﾄ・記録されているか？', 'Are engine room emergency stops for ventilation fans clearly marked and do records indicate that they have been regularly tested?', 21);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.22', 'ﾃﾞｨｰｾﾞﾙ機関の、高圧・低圧用燃料供給ﾗｲﾝは適切に被覆又は保護されているか？', 'Are diesel engine high and low pressure fuel delivery pipes adequately jacketed or screened?', 22);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.23', '燃料油・潤滑油ﾗｲﾝ付近にある排気管や他の高熱表面は、飛沫に対して保護されているか？', 'Are diesel engine exhausts and other hot surfaces in the vicinity of fuel, diesel, lubricating and hydraulic oil pipes protected against spray?', 23);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.24', '特にﾃﾞｨｰｾﾞﾙ機関の場合、高熱の表面に燃料油・潤滑油が付着した形跡は認められないか？', 'Are hot surfaces, particularly diesel engines, free of any evidence of fuel, diesel and lubricating oil?', 24);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.25', '清浄機室及び燃料油・潤滑油を取り扱う区画は、十分に換気され清潔であるか？', 'Are purifier rooms and fuel and lubricating oil handling areas ventilated and clean?', 25);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.26', '船舶が船級記載でUMSである場合、主機ﾍﾞｱﾘﾝｸﾞ温度ﾓﾆﾀｰ又はｸﾗﾝｸｹｰｽのｵｲﾙﾐｽﾄ検知器は良好な状態であるか？', 'If the vessel class notation allowing UMS operation, are main engine bearing temperature monitors, or the crankcase oil mist detector, in good order?', 26);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.27', '主機のある区画内に集合油圧ﾎﾟﾝﾌﾟがある場合、ｵｲﾙﾐｽﾄ検知器が設置されているか？', 'Where hydraulic aggregate pumps are located within the main engine compartment, is an oil mist detectors fitted?', 27);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.28', '主配電盤・発電機・他の電気機器類は、水の飛沫から保護されているか？', 'Are the main switchboard, alternators and other electrical equipment satisfactorily protected from water spray?', 28);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.29', '中電圧配電盤（例えば220V以上）の前部及び後部に絶縁物（ﾏｯﾄ）が用意されており、それらは良好な状態であるか？', 'Is deck insulation provided to the front and rear of medium power (i.e. 220V and above) electrical switchboards and is it in good order?', 29);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.30', '油ﾀﾝｸのｶﾞﾗｽｹﾞｰｼﾞ自動閉止装置はﾌｪｲﾙｾｰﾌ型であるか？又、目詰まりしていないか？', 'Are gauge glass closing devices on oil tanks of a self-closing, fail-safe type and not inhibited?', 30);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.31', '二重底ﾀﾝｸの自動閉鎖測深装置は良好な状態であり、閉鎖されているか？', 'Are self-closing sounding devices to double bottom tanks in good order and closed?', 31);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.32', '全ての移動式機器類は、危険を生じ得る場合は、有効な保護措置が講じられているか？', 'Is all moving machinery provided with effective guards where this presents a hazard?', 32);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.33', '機関室の工具類は適切な眼を保護するための対策が備えられているか？', 'Do engine room machine tools have adequate eye protection available?', 33);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.34', '吊り上げ装置の、定期的な検査・ﾃｽﾄの記録が用意されているか？', 'Are records maintained for the regular inspection and testing of lifting devices?', 34);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.35', 'ﾜｲﾔｰｽﾘﾝｸﾞ類等、他の吊り上げ用具についても適切な検査・整備計画が用意されているか？', 'Is an inspection and maintenance programme in place for other lifting equipment such as wire slings?', 35);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.36', '機関室・倉庫・操舵機室の全ての移動物は、適切に固縛されているか？', 'Is all loose gear in the machinery spaces, stores and steering compartment properly secured?', 36);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.37', '機関室区画・操舵機室は清潔で漏洩もなく、全般的な管理水準・整備状況は良好であるか？', 'Are machinery spaces and steering compartments clean and free from obvious leaks and is the overall standard of housekeeping and fabric maintenance satisfactory?', 37);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.38', 'ﾋﾞﾙｼﾞには、油・ゴミ・沈殿物が含まれていないか？', 'Are bilges free of oil, rubbish and sediment?', 38);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.39', 'ﾋﾞﾙｼﾞｱﾗｰﾑは定期的にﾃｽﾄ・記録されているか？', 'Is the bilge high level alarm system regularly tested and are records maintained?', 39);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.40', '海水ﾎﾟﾝﾌﾟ・ｼｰﾁｪｽﾄ・関連配管は良好な状態であり、固い錆や仮修理の形跡はないか？（特に船外弁から外側）', 'Are seawater pumps, sea chests and associated pipework in good order and free of hard rust and temporary repairs, particularly outboard of the ship-side valves?', 40);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.41-1', '下記の機器が装備されている場合、全て良好な状態であり、整備も行き届いているか？(主機)', 'Are the following, where applicable, all in good order and do they appear to be well maintained?(The main engine)', 41);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.41-2', '下記の機器が装備されている場合、全て良好な状態であり、整備も行き届いているか？(補機・発電機（装備されている場合は軸発・非常用発電機も含む）)', 'Are the following, where applicable, all in good order and do they appear to be well maintained?(Auxiliary engines and generators, including a shaft and emergency generators where fitted)', 42);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.41-3', '下記の機器が装備されている場合、全て良好な状態であり、整備も行き届いているか？(ﾎﾞｲﾗｰ類)', 'Are the following, where applicable, all in good order and do they appear to be well maintained?(Boilers, including waste heat and domestic boilers)', 43);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.41-4', '下記の機器が装備されている場合、全て良好な状態であり、整備も行き届いているか？(圧縮機・非常用圧縮機（計装含む）)', 'Are the following, where applicable, all in good order and do they appear to be well maintained?(Compressors including main, instrument and emergency air compressors)', 44);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.41-5', '下記の機器が装備されている場合、全て良好な状態であり、整備も行き届いているか？(清浄機・燃料関連機器)', 'Are the following, where applicable, all in good order and do they appear to be well maintained?(Purifiers and fuel oil handling equipment)', 45);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.41-6', '下記の機器が装備されている場合、全て良好な状態であり、整備も行き届いているか？(ﾌｧﾝ・ｽｸﾗﾊﾞｰ・検知器・ﾊﾞﾙﾌﾞを含む、IGS全般)', 'Are the following, where applicable, all in good order and do they appear to be well maintained?(Inert gas plant, including the fans, scrubber, analyser and valves)', 46);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.41-7', '下記の機器が装備されている場合、全て良好な状態であり、整備も行き届いているか？(汚水処理装置)', 'Are the following, where applicable, all in good order and do they appear to be well maintained?(Sewage plant)', 47);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.41-8', '下記の機器が装備されている場合、全て良好な状態であり、整備も行き届いているか？(ﾋﾞﾙｼﾞ配管・油水分離機)', 'Are the following, where applicable, all in good order and do they appear to be well maintained?(Bilge pumping arrangements and the oily water separator)', 48);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.41-9', '下記の機器が装備されている場合、全て良好な状態であり、整備も行き届いているか？(蒸気・燃料・潤滑油・海水・汚水処理・ﾄﾞﾚﾝ・ｴｱｰﾊﾟｲﾌﾟなどを含むﾊﾟｲﾌﾟﾗｲﾝ)', 'Are the following, where applicable, all in good order and do they appear to be well maintained?(Pipework, including steam, fuel, lubricating oil, seawater, sewage, drain and air pipes, etc.)', 49);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.41-10', '下記の機器が装備されている場合、全て良好な状態であり、整備も行き届いているか？(冷凍・空調機器)', 'Are the following, where applicable, all in good order and do they appear to be well maintained?(Refrigeration and air conditioning machinery)', 50);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.41-11', '下記の機器が装備されている場合、全て良好な状態であり、整備も行き届いているか？(油圧発生ﾎﾟﾝﾌﾟ)', 'Are the following, where applicable, all in good order and do they appear to be well maintained?(Hydraulic aggregate pumps)', 51);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.41-12', '下記の機器が装備されている場合、全て良好な状態であり、整備も行き届いているか？(換気ﾌｧﾝ・ﾀﾞｸﾄ)', 'Are the following, where applicable, all in good order and do they appear to be well maintained?(Ventilation fans and trunking)', 52);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.41-13', '下記の機器が装備されている場合、全て良好な状態であり、整備も行き届いているか？(船尾管ｼｰﾙ装置)', 'Are the following, where applicable, all in good order and do they appear to be well maintained?(Stern tube sealing arrangements)', 53);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.41-14', '下記の機器が装備されている場合、全て良好な状態であり、整備も行き届いているか？(予備用を含むその他の機器)', 'Are the following, where applicable, all in good order and do they appear to be well maintained?(Any other items of machinery, including stand-by machinery.)', 54);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.41-15', '下記の機器が装備されている場合、全て良好な状態であり、整備も行き届いているか？(ﾊﾞｰﾅｰ・ﾁｭｰﾌﾞ・取入れ口・排気ﾏﾆﾎｰﾙﾄﾞ・ｽﾊﾟｰｸｱﾚｽﾀｰ（ある場合）)', 'Are the following, where applicable, all in good order and do they appear to be well maintained?(Burners, tubes, uptakes, exhaust manifolds and spark arrestors (If fitted))', 55);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.42', '機関士は緊急時に船橋から機関操作を受け継ぐ場合の手順を理解しているか？', 'Are engineers familiar with the procedure for taking over the controlsfor manoeuvring the vessels from the bridge in an emergency? ', 56);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.43', '非常用発電機の簡潔な起動操作説明が明瞭に掲示されているか？', 'Are concise starting instructions for the emergency generator clearly displayed?', 57);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.44', '非常用発電機の燃料貯蔵ﾀﾝｸは十分な量が確保されているか？', 'Is the emergency generator reserve fuel tank provided with sufficient fuel?', 58);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.45', '非常用発電機が設置されていない場合、非常用ﾊﾞｯﾃﾘｰは良好な状態で、完全に充電されているか？', 'Where an emergency generator is not fitted, are engine room emergency batteries in good order and fully charged?', 59);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.46', '電気ｹｰﾌﾞﾙや接続箱を含め、全ての電気機器は良好な状態であるか？', 'Is all electrical equipment including junction boxes and cable runs in good order?', 60);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.47', '配電盤には顕著な絶縁不良はないか？', 'Are switchboards free of significant earth faults?', 61);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.48', '非常用操舵装置は過去3ヵ月以内にﾃｽﾄされ、結果が記録されているか？', 'Has the emergency steering gear been tested within the past three months and are the results recorded?', 62);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.49', '非常操舵装置の切替え手順は、操舵区画及び船橋操舵室に明瞭に表示されているか？', 'Are emergency steering gear changeover procedures clearly displayed in the steering compartment and in the wheelhouse?', 63);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.50', '職員は、非常操舵装置の操作を理解しているか？', 'Are officers familiar with operation of the steering gear in the emergency mode?', 64);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.51', '操舵装置の非常用ﾘｻﾞｰﾌﾞﾀﾝｸは、満量になるまで補充されているか？', 'Is the steering gear emergency reserve tank fully charged?', 65);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.52', '船首方位情報を提供する手段は十分であるか？', 'Are the arrangements for the provision of heading information adequate? ', 66);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.53', '船橋との通信連絡手段は良好であるか？', 'Are communications with the bridge satisfactory?', 67);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.54', '非常操舵位置にて、舵角指示器は明瞭に見ることが出来るか？', 'Is the rudder angle indicator clearly visible at the emergency steering position?', 68);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.55', '操舵位置への接近を妨げるものはないか？', 'Is access to steering gear unobstructed?', 69);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (14, '11.56', '操舵機室区画は、適切な手摺・ｸﾞﾚｰﾁﾝｸﾞ又は床の滑り止めの措置が講じられているか？', 'Is the steering compartment fitted with suitable handrails, gratings or other non-slip surfaces?', 70);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.1', '船体の全般的状態・外観・清浄度は良好な状態であるか？', 'Is the general condition, visual appearance and cleanliness of the hull satisfactory?', 1);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.2', '船体外板のﾏｰｷﾝｸﾞの位置は正確で明瞭であるか？', 'Are hull markings clearly indicated and correctly placed?', 2);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.3', '曝露甲板の全般的状態・外観・清浄度は良好な状態であるか？', 'Is the general condition, visual appearance and cleanliness of the weather decks satisfactory?', 3);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.4', '甲板上の作業区域は、明瞭に判るように滑り止めが施されているか？', 'Do decks in working areas have clearly identified non-slip surfaces?', 4);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.5', '配管は全般的に良好で、著しい腐食・ﾋﾟｯﾁﾝｸﾞ・応急修理或いは仮修理の箇所はないか？', 'Is the general condition of service pipework satisfactory and is it free from significant corrosion and pitting and soft patches or other temporary repairs?', 5);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.6', 'ﾊﾟｲﾌﾟｽﾀﾝﾄﾞ・ｸﾗﾝﾌﾟ・ｻﾎﾟｰﾄ・伸縮ｼﾞｮｲﾝﾄは良好な状態であるか？', 'Are pipe stands, clamps, supports and expansion arrangements satisfactory?', 6);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.7', '全ての甲板開口部（水蜜ﾄﾞｱ及びﾎﾟｰﾄﾎｰﾙを含む）は良好な状態であり、確実に係止可能であるか？', 'Are all deck openings, including watertight doors and portholes, in good order and capable of being properly secured?', 7);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.8', '燃料・ﾊﾞﾗｽﾄ・その他の区画のﾍﾞﾝﾄやｴｱｰﾊﾟｲﾌﾟは良好な状態で、定期的な整備が行なわれているか？', 'Are fuel, ballast and other space vents and air pipes in good order and does visual evidence indicate regular maintenance?', 8);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.9', '全てのﾍﾞﾝﾄ・ｴｱｰﾊﾟｲﾌﾟは、各々の供用ｽﾍﾟｰｽを明瞭に表示しているか？', 'Are all vents and air pipes clearly marked to indicate the spaces they serve?', 9);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.10', '上部構造物の全般的状態・外観・清浄度は良好な状態であるか？', 'Is the general condition, visual appearance and cleanliness of the superstructure satisfactory?', 10);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.11', '甲板の照明は適切であるか？', 'Is deck lighting adequate?', 11);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.12', '電線管・配線を含む、電気設備の一般的状態は良好であるか？', 'Is the general condition of electrical equipment, including conduits and wiring, satisfactory?', 12);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.13', 'ｶﾞｽ危険区域にある照明具は、完全防爆型で、良好な状態であるか？', 'Are light fittings in gas-hazardous areas Ex ''d'' rated and in good order?', 13);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.14', '各ｽﾄｱｰ及び内部ｽﾍﾟｰｽは、清潔で整理整頓が行き届いているか？', 'Are internal spaces and storerooms clean, free from debris and tidy?', 14);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.15', 'ﾌｫｯｸｽﾙ内部に残水はないか？', 'Is the forecastle space free of water?', 15);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.16', '全ての通路に障害物はなく、出口は明瞭に表示されているか？', 'Are alleyways free of obstructions and exits clearly marked?', 16);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.17', '喫煙室・食堂・サニタリー・食品庫・食糧取扱ｽﾍﾟｰｽ・冷凍庫・厨房などの区域はを含む居住区及び公室は清潔で整頓され衛生的にも良好な状態であるか？', 'Are accommodation, public spaces, including smoke rooms, mess rooms, sanitary areas, food storerooms, food handling spaces, refrigerated spaces, galleys and pantries clean, tidy and in a hygienically condition?', 17);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.18', '洗濯場には、火災の原因となる衣服類が放置されていないか？', 'Are laundries free of accumulations of clothing that could constitute a fire hazard?', 18);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.19', '病室が設置されている場合、清潔で整頓され、使用準備が整えられているか？', 'If fitted, is the Ship’s Hospital clean and tidy and ready for use?', 19);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.20', '居住区内の照明の明るさは十分であるか？', 'Is the level of accommodation lighting satisfactory?', 20);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.21', '居住区内の電気設備の状態は良好であるか？', 'Is the condition of electrical equipment in the accommodation　satisfactory? ', 21);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (15, '12.22', '冷蔵室内の呼出し警報装置は良好な状態であり、作動するか？', 'Are personnel alarms in refrigerated spaces in good order and operational?', 22);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.1', '氷の中での操作のための手順書が準備されているか？', 'Are procedures available for operations in ice?', 1);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.2', '氷を発見する手段が装備されているか？', 'Are means in place to detect ice?', 2);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.3', '氷の状況、氷海海図、及び人工衛星からの映像を含む、航海・地理学・環境ﾃﾞｰﾀを定常的に受領するｼｽﾃﾑが構築されているか？', 'Are system in place for the routine receipt of navigatgional, meteorological and environmental data including ice data, ice charts and satellite images?', 3);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.4', '船舶の乗組員全員に対する、氷海航行の特別な訓練が用意されているか?', 'Has training specifically addressing navigation in ice been provided to members of the vessel’s complement?', 4);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.5', '少なくとも一台の主機海水取り入れ弁が氷結または作動不良を防ぐための手段が採られているか？', 'Are means in place on at least one main engine sea water chest to prevent its freezing or clogging?', 5);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.6', '氷点下における操作手順書が確立されているか？', 'Are procedures available for operations in sub-zero temperatures?', 6);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.7', '乗組員を氷点下に晒すことをさけるための手段及び/或いは手順が確立されているか？', 'Are means and/or procedures in place to protect personnel from exposure to sub-zero temperatrues?', 7);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.8', '居住区を居住に適切な温度維持する装置が準備されているか？', 'Are means provided to maintain accommodation spaces at a temperature suitable for habitation?', 8);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.9', '氷点下状態において、本船への安全な乗船及び本船上での移動を確保する手順が準備されているか？', 'Are means and procedures in place to ensure safe access and movement about the vessel in sub-zero conditions?', 9);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.10', '船橋の窓は凍結防止対策がとられているか？', 'Are means in place to prevent the icing of wheelhouse windows?', 10);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.11', '氷点下の温度でも適切に作動する旨の船級の型式承認ﾚｰﾀﾞｰが装備されているか？', 'Are radars fitted that are of a type classed as being suitable for operation in sub zero temperatures？', 11);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.12', '空気駆動の汽笛及び霧中信号機は氷点下において作動可能であることを確認する手段及び/または手順が用意されているか？', 'Are means and/or procedures in palce to ensure that air driven whistles and fog horns are operable at sub-zero temperatures?', 12);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.13', '重要な機器及び装置が氷点下で操作可能であることを確認する手段及び又は手順が準備されているか？', 'Are means and/or procedures in place to ensure the operability of critical equipment and systems in sub-zero air temperatures? Procedures should address the need to prepare critical equipment prior to entering the cold weather area. Particular attention should be given to ensuring the suitability of fuels used in systems such as those serving the emergency generator and lifeboat engine.', 13);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.14', '船橋の両ｳｨﾝｸﾞ及び船首に、作動するｻｰﾁﾗｲﾄが用意されているか？', 'Are means and/or procedures in place aimed at ensuring the ready availablitity of life saving appliances?', 14);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.15', '消火設備の操作可能の確認を目的とする手段、及び/または手順が準備されているか？', 'Are means and/or procedures in place aimed at ensuring the operability of fire-fighitng systems?', 15);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.16', '空気取り入れ口及びﾌｧｲｱｰﾌﾗｯﾌﾟの適切な機能を確かめる手段及び/又は手順が準備されているか？', 'Are means and/or procedures in place to ensure the proper functioning of air intakes and fire flaps?', 16);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.17', 'ﾃﾞｯｷのﾊﾟｲﾌﾟﾗｲﾝを凍結の危険から保護する手段及び/または手順が準備されているか？', 'Are means and/or procedurs in place to protect piping systems on deck from the risk of freezing?', 17);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.18', 'ﾊﾞﾗｽﾄｼｽﾃﾑ及び海水供給ｼｽﾃﾑが海水温度2℃及び氷点下の大気温度のもとで作動可能を確認する手段及び/又は手順が準備されているか？', 'Are means and/or procedures in place to ensure the operability of ballast systems and any drenching system at sea temperature of -2℃ and sub-zero air temperature?', 18);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.19', 'ｶｰｺﾞﾀﾝｸの一次及び二次通気装置の氷結を防ぐ手段が採られているか？', 'Are means or procedures in place to prevent the icing up of cargo tank primary and secondary venting arrangements?', 19);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.20', '主機及び重要な補機の操作に必要なｾｯﾄﾘﾝｸﾞ及びｻｰﾋﾞｽﾀﾝｸのｴｱｰ抜きが氷結することを防ぐ手段及び/また手順がとられているか？', 'Are means and/or procedurs in place to prevent the icing up of air pipes to settling and service tanks required for the operation of the main propulsion plant and essential auxiliaries?', 20);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (16, '13.21', '氷点下における作業に対する特別な訓練が乗組員に用意されているか？', 'Has training specifically addressing operations in sub-zero temperatures been provided to the vessel’s complement?', 21);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (17,'1.1','船名','Name of the vessel',1);
INSERT INTO ms_viq_no(viq_code_id, viq_no, description, description_eng, order_no) VALUES (17,'1.2','船舶IMO番号','Vessel IMO number',2);
