databaseConfig.sql:
select DM_KANRYO_INFO.DM_KANRYO_INFO_ID , DM_KANRYO_INFO.KANRYO_DATE , DM_KANRYO_INFO.MS_USER_ID , DM_KANRYO_INFO.LINK_SAKI , DM_KANRYO_INFO.LINK_SAKI_ID ,
DM_KANRYO_INFO.DELETE_FLAG , DM_KANRYO_INFO.SEND_FLAG , DM_KANRYO_INFO.VESSEL_ID , DM_KANRYO_INFO.DATA_NO , DM_KANRYO_INFO.USER_KEY , DM_KANRYO_INFO.RENEW_DATE , DM_KANRYO_INFO.RENEW_USER_ID , DM_KANRYO_INFO.TS
from DM_KANRYO_INFO WHERE (DM_KANRYO_INFO.DELETE_FLAG = 0 or DM_KANRYO_INFO.DELETE_FLAG is NULL)
