select MS_DM_TEMPLATE_FILE.MS_DM_TEMPLATE_FILE_ID , MS_DM_TEMPLATE_FILE.UPDATE_DATE , MS_DM_TEMPLATE_FILE.TEMPLATE_FILE_NAME , MS_DM_TEMPLATE_FILE.DATA , MS_DM_TEMPLATE_FILE.MS_DM_HOUKOKUSHO_ID ,
MS_DM_TEMPLATE_FILE.DELETE_FLAG , MS_DM_TEMPLATE_FILE.SEND_FLAG , MS_DM_TEMPLATE_FILE.VESSEL_ID , MS_DM_TEMPLATE_FILE.DATA_NO , MS_DM_TEMPLATE_FILE.USER_KEY , MS_DM_TEMPLATE_FILE.RENEW_DATE , MS_DM_TEMPLATE_FILE.RENEW_USER_ID , MS_DM_TEMPLATE_FILE.TS
from MS_DM_TEMPLATE_FILE WHERE (MS_DM_TEMPLATE_FILE.DELETE_FLAG = 0 or MS_DM_TEMPLATE_FILE.DELETE_FLAG is NULL)
