select SI_KOUSHU_ATTACH_FILE.SI_KOUSHU_ATTACH_FILE_ID , SI_KOUSHU_ATTACH_FILE.UPDATE_DATE , SI_KOUSHU_ATTACH_FILE.FILE_NAME , SI_KOUSHU_ATTACH_FILE.DATA , SI_KOUSHU_ATTACH_FILE.SI_KOUSHU_ID ,
SI_KOUSHU_ATTACH_FILE.DELETE_FLAG , SI_KOUSHU_ATTACH_FILE.SEND_FLAG , SI_KOUSHU_ATTACH_FILE.VESSEL_ID , SI_KOUSHU_ATTACH_FILE.DATA_NO , SI_KOUSHU_ATTACH_FILE.USER_KEY , SI_KOUSHU_ATTACH_FILE.RENEW_DATE , SI_KOUSHU_ATTACH_FILE.RENEW_USER_ID , SI_KOUSHU_ATTACH_FILE.TS
from SI_KOUSHU_ATTACH_FILE WHERE (SI_KOUSHU_ATTACH_FILE.DELETE_FLAG = 0 or SI_KOUSHU_ATTACH_FILE.DELETE_FLAG is NULL)
