select SI_REMARKS_ATTACH_FILE.SI_REMARKS_ATTACH_FILE_ID , SI_REMARKS_ATTACH_FILE.UPDATE_DATE , SI_REMARKS_ATTACH_FILE.FILE_NAME , SI_REMARKS_ATTACH_FILE.DATA , SI_REMARKS_ATTACH_FILE.SI_REMARKS_ID ,
SI_REMARKS_ATTACH_FILE.DELETE_FLAG , SI_REMARKS_ATTACH_FILE.SEND_FLAG , SI_REMARKS_ATTACH_FILE.VESSEL_ID , SI_REMARKS_ATTACH_FILE.DATA_NO , SI_REMARKS_ATTACH_FILE.USER_KEY , SI_REMARKS_ATTACH_FILE.RENEW_DATE , SI_REMARKS_ATTACH_FILE.RENEW_USER_ID , SI_REMARKS_ATTACH_FILE.TS
from SI_REMARKS_ATTACH_FILE WHERE (SI_REMARKS_ATTACH_FILE.DELETE_FLAG = 0 or SI_REMARKS_ATTACH_FILE.DELETE_FLAG is NULL)
