select SI_SHOBYO_ATTACH_FILE.SI_SHOBYO_ATTACH_FILE_ID , SI_SHOBYO_ATTACH_FILE.UPDATE_DATE , SI_SHOBYO_ATTACH_FILE.FILE_NAME , SI_SHOBYO_ATTACH_FILE.DATA , SI_SHOBYO_ATTACH_FILE.SI_SHOBYO_ID ,
SI_SHOBYO_ATTACH_FILE.DELETE_FLAG , SI_SHOBYO_ATTACH_FILE.SEND_FLAG , SI_SHOBYO_ATTACH_FILE.VESSEL_ID , SI_SHOBYO_ATTACH_FILE.DATA_NO , SI_SHOBYO_ATTACH_FILE.USER_KEY , SI_SHOBYO_ATTACH_FILE.RENEW_DATE , SI_SHOBYO_ATTACH_FILE.RENEW_USER_ID , SI_SHOBYO_ATTACH_FILE.TS
from SI_SHOBYO_ATTACH_FILE WHERE (SI_SHOBYO_ATTACH_FILE.DELETE_FLAG = 0 or SI_SHOBYO_ATTACH_FILE.DELETE_FLAG is NULL)
